import * as React from 'react';
import {
  Table,
  TableBody,
  TableCell,
  TableContainer,
  TableHead,
  TableRow,
  Paper,
  Box,
  Typography,
  Avatar,
  useTheme,
  Container,
  useMediaQuery,
  Chip,
} from '@mui/material';

// Hilfsfunktion zur Erstellung der Daten
// Die Anzahl der Meisterschaften wird automatisch aus der Länge des "years"-Arrays berechnet
const createChampionData = (id, name, logoColor, years) => {
  return {
    id,
    name,
    logoColor,
    years: years.sort((a, b) => b - a), // Jahre absteigend sortieren
    championships: years.length,
  };
};

// Beispieldaten für die Ehrentafel
const hallOfFameData = [
  createChampionData(1, 'Rakete Freiburg', '#D32F2F', [2024, 2022, 2020]),
  createChampionData(2, 'Lokomotive Littenweiler', '#5D4037', [2023, 2019]),
  createChampionData(3, 'Die 2. Wahl', '#757575', [2021]),
  createChampionData(4, 'Red Sea', '#C2185B', [2018]),
  createChampionData(5, 'Dynamo Tresen', '#1976D2', [2017, 2015, 2014, 2012]),
  createChampionData(6, 'Wurschtwecklebombers', '#00796B', [2016, 2013]),
];

// Eine wiederverwendbare, gestylte Tabellenzelle
const StyledTableCell = ({ children, sx, align, ...props }) => {
  const theme = useTheme();
  return (
    <TableCell
      align={align}
      sx={{
        color: theme.palette.grey[300],
        fontFamily: 'comfortaa',
        borderBottom: `1px solid ${theme.palette.grey[800]}`,
        py: 1.5,
        ...sx,
      }}
      {...props}
    >
      {children}
    </TableCell>
  );
};

const HallOfFame = ({ title }) => {
  const theme = useTheme();
  const isMobile = useMediaQuery(theme.breakpoints.down('sm'));

  // Sortiere die Daten nach Anzahl der Meisterschaften (absteigend)
  const sortedChampions = React.useMemo(() => {
    return [...hallOfFameData].sort((a, b) => b.championships - a.championships);
  }, []);

  return (
    <Container maxWidth="lg" sx={{ my: 4, px: isMobile ? 1 : 2 }}>
      <Typography
        variant={isMobile ? 'h5' : 'h4'}
        sx={{
          mb: 3,
          mt: 2,
          color: '#00A99D',
          fontWeight: 700,
          fontFamily: 'comfortaa',
          textAlign: 'center',
          textTransform: 'uppercase',
          letterSpacing: '0.1em',
        }}
      >
        {title}
      </Typography>
      <TableContainer
        component={Paper}
        sx={{
          backgroundColor: '#111',
          borderRadius: theme.shape.borderRadius,
          border: `1px solid ${theme.palette.grey[800]}`,
        }}
      >
        <Table aria-label="Ehrentafel der Meister">
          <TableHead>
            <TableRow sx={{ backgroundColor: 'rgba(255,255,255,0.05)' }}>
              <StyledTableCell sx={{ fontWeight: 'bold', color: theme.palette.grey[100], width: '10%' }}>Rang</StyledTableCell>
              <StyledTableCell sx={{ fontWeight: 'bold', color: theme.palette.grey[100], width: '30%' }}>Team</StyledTableCell>
              <StyledTableCell align="center" sx={{ fontWeight: 'bold', color: theme.palette.grey[100], width: '15%' }}>Titel</StyledTableCell>
              {!isMobile && (
                <StyledTableCell sx={{ fontWeight: 'bold', color: theme.palette.grey[100], width: '45%' }}>Meisterjahre</StyledTableCell>
              )}
            </TableRow>
          </TableHead>
          <TableBody>
            {sortedChampions.map((team, index) => (
              <TableRow
                key={team.id}
                sx={{ '&:hover': { backgroundColor: 'rgba(255,255,255,0.08)' } }}
              >
                <StyledTableCell component="th" scope="row" sx={{ fontWeight: 'bold', fontSize: '1.1rem', color: theme.palette.grey[100] }}>
                  {index + 1}
                </StyledTableCell>
                <StyledTableCell>
                  <Box sx={{ display: 'flex', alignItems: 'center', flexDirection: isMobile ? 'column' : 'row', alignItems: isMobile ? 'flex-start' : 'center' }}>
                    <Avatar
                      alt={`${team.name} Logo`}
                      sx={{
                        width: 40,
                        height: 40,
                        mr: isMobile ? 0 : 2,
                        mb: isMobile ? 1 : 0,
                        color: theme.palette.getContrastText(team.logoColor || theme.palette.grey[700]),
                        backgroundColor: team.logoColor || theme.palette.grey[700],
                      }}
                    >
                      {team.name.substring(0, 1).toUpperCase()}
                    </Avatar>
                    <Box>
                      <Typography variant="h6" sx={{ fontFamily: 'comfortaa', color: theme.palette.grey[100], fontSize: '1rem', fontWeight: 600 }}>
                        {team.name}
                      </Typography>
                       {/* Auf Mobilgeräten werden die Jahre unter dem Teamnamen angezeigt */}
                      {isMobile && (
                        <Box sx={{ display: 'flex', flexWrap: 'wrap', gap: 0.5, mt: 1 }}>
                          {team.years.map((year) => (
                            <Chip key={year} label={year} size="small" sx={{ fontFamily: 'comfortaa', backgroundColor: theme.palette.grey[700], color: theme.palette.grey[200] }} />
                          ))}
                        </Box>
                      )}
                    </Box>
                  </Box>
                </StyledTableCell>
                <StyledTableCell align="center">
                  <Typography variant="h6" sx={{ fontFamily: 'comfortaa', color: theme.palette.grey[100], fontWeight: 'bold' }}>
                    {team.championships}
                  </Typography>
                </StyledTableCell>
                {/* Die Spalte "Meisterjahre" wird nur auf dem Desktop angezeigt */}
                {!isMobile && (
                  <StyledTableCell>
                    <Box sx={{ display: 'flex', flexWrap: 'wrap', gap: 0.5 }}>
                      {team.years.map((year) => (
                        <Chip key={year} label={year} sx={{ fontFamily: 'comfortaa', fontWeight: 'bold', backgroundColor: theme.palette.grey[700], color: theme.palette.grey[200] }} />
                      ))}
                    </Box>
                  </StyledTableCell>
                )}
              </TableRow>
            ))}
          </TableBody>
        </Table>
      </TableContainer>
    </Container>
  );
};

export default HallOfFame;
