import * as React from 'react';
import { Box, Container, IconButton, Typography, useTheme } from '@mui/material';
import ArrowBackIosNewIcon from '@mui/icons-material/ArrowBackIosNew';
import ArrowForwardIosIcon from '@mui/icons-material/ArrowForwardIos';
import NewsCard from './NewsCard.js'; // Sicherstellen, dass der Pfad korrekt ist
import { useSwipeable } from 'react-swipeable'; // Import für Swipe-Gesten

// Beispieldaten für die News
const newsData = [
  {
    date: '31. Mai 2025',
    title: 'Saisonstart terminiert!',
    subtitle: 'Die neue Saison der Bunten Liga Freiburg beginnt am ersten Septemberwochenende. Alle Teams sind aufgerufen, ihre Kaderlisten bis Ende August einzureichen.',
    content: 'Nach einer langen und erholsamen Sommerpause freuen wir uns, den offiziellen Start der Saison 2025/2026 bekannt zu geben. Der erste Spieltag findet am Samstag, den 6. September statt. Die Spielpläne werden in den kommenden Wochen veröffentlicht. Wir bitten alle Team-Kapitäne, die Verfügbarkeit ihrer Spieler zu prüfen und die finalen Kader bis zum 31. August über das Online-Portal zu melden. Wir wünschen allen eine spannende und faire Saison!',
  },
  {
    date: '28. Mai 2025',
    title: 'Neues Regelwerk für Fair-Play',
    subtitle: 'Um den fairen Wettbewerb weiter zu fördern, wurden einige Anpassungen im Regelwerk vorgenommen, die ab sofort gelten.',
    content: 'Das Liga-Komitee hat in Zusammenarbeit mit den Team-Vertretern ein überarbeitetes Fair-Play-Regelwerk verabschiedet. Die wichtigste Änderung betrifft die Handhabung von Zeitstrafen, die nun konsequenter geahndet werden. Zudem wird die Fair-Play-Wertung am Ende der Saison ein höheres Gewicht haben. Alle Details zu den neuen Regeln findet ihr im Download-Bereich auf der Webseite. Fairness und Respekt auf und neben dem Platz sind die Grundpfeiler unserer Liga.',
  },
  {
    date: '25. Mai 2025',
    title: 'Team-Treffen im Juli',
    subtitle: 'Einladung zum jährlichen Treffen aller Mannschafts-Kapitäne am 15. Juli im Jos-Fritz-Café.',
    content: 'Wie jedes Jahr möchten wir alle Kapitäne und Co-Kapitäne zu unserem jährlichen Treffen einladen. Dieses Jahr findet es am 15. Juli um 19:00 Uhr im Jos-Fritz-Café statt. Auf der Agenda stehen unter anderem die Vorstellung neuer Teams, die Diskussion über das neue Regelwerk und die Planung von gemeinsamen Events. Für Getränke ist gesorgt. Wir freuen uns auf einen konstruktiven Austausch und zahlreiches Erscheinen.',
  },
];

const NewsCarousel = () => {
  const theme = useTheme();
  const [activeIndex, setActiveIndex] = React.useState(0);

  // WICHTIG: Hooks müssen vor allen bedingten Returns aufgerufen werden.
  const handlePreviousCallback = React.useCallback(() => {
    // Verhindert das Weiterschalten, wenn der erste Index erreicht ist
    // und newsData vorhanden ist
    if (newsData && newsData.length > 0) {
        setActiveIndex((prevIndex) => Math.max(0, prevIndex - 1));
    }
  }, []); // newsData als Abhängigkeit, falls es sich ändern könnte (hier konstant)

  const handleNextCallback = React.useCallback(() => {
    // Verhindert das Weiterschalten, wenn der letzte Index erreicht ist
    // und newsData vorhanden ist
    if (newsData && newsData.length > 0) {
        setActiveIndex((prevIndex) => Math.min(newsData.length - 1, prevIndex + 1));
    }
  }, []); // newsData als Abhängigkeit

  // Swipe-Handler konfigurieren
  const swipeHandlers = useSwipeable({
    onSwipedLeft: () => handleNextCallback(),
    onSwipedRight: () => handlePreviousCallback(),
    preventScrollOnSwipe: true, // Verhindert das Scrollen der Seite während des Swipens
    trackMouse: true // Ermöglicht auch das Swipen mit der Maus (für Desktop-Testzwecke)
  });

  // Sicherheitscheck für newsData (kann jetzt nach den Hooks erfolgen)
  if (!newsData || newsData.length === 0) {
    return (
      <Container maxWidth="xl" sx={{ my: 4, textAlign: 'center' }}>
        <Typography variant="h6" sx={{fontFamily:'comfortaa'}}>Keine Nachrichten verfügbar.</Typography>
      </Container>
    );
  }

  const currentNews = newsData[activeIndex];

  // Zusätzlicher Sicherheitscheck für currentNews
  if (!currentNews) {
    // Dieser Fall sollte bei gültigem newsData und activeIndex nicht eintreten,
    // aber es ist eine gute Absicherung.
    return (
        <Container maxWidth="xl" sx={{ my: 4, textAlign: 'center' }}>
            <Typography variant="h6" color="error" sx={{fontFamily:'comfortaa'}}>Fehler beim Laden der Nachricht.</Typography>
        </Container>
    );
  }

  const isFirstNews = activeIndex === 0;
  const isLastNews = activeIndex === newsData.length - 1;

  return (
    <Container maxWidth="xl" sx={{ my: 4, px: { xs: 1, sm: 2 } }}>
      {/* Container für die NewsCard, jetzt mit Swipe-Handlern */}
      <Box 
        {...swipeHandlers} // Swipe-Handler auf diesen Container anwenden
        sx={{ 
          width: '100%', 
          maxWidth: '900px', 
          mx: 'auto',
          cursor: 'grab', // Zeigt an, dass das Element verschiebbar ist
          '&:active': {
            cursor: 'grabbing', // Ändert den Cursor beim "Greifen"
          }
        }}
      >
        <NewsCard
          key={activeIndex} 
          title={currentNews.title}
          subtitle={currentNews.subtitle}
          date={currentNews.date}
          content={currentNews.content}
        />
      </Box>

      {/* Container für Navigationsbuttons unter der Karte */}
      <Box 
        sx={{ 
          display: 'flex', 
          justifyContent: 'center', // Buttons zentriert
          alignItems: 'center',
          mt: 2, // Abstand zur Karte (vorher mb)
          width: '100%', 
          maxWidth: '900px', 
          mx: 'auto', 
          gap: 2, // Abstand zwischen den Buttons
        }}
      >
        <IconButton 
            onClick={handlePreviousCallback} 
            aria-label="vorherige nachricht"
            disabled={isFirstNews} // Deaktiviert, wenn erste Nachricht
            sx={{ 
                color: isFirstNews ? 'grey.700' : 'grey.400', // Ausgegraut, wenn deaktiviert
                backgroundColor: 'rgba(255,255,255,0.05)',
                '&:hover': { 
                    backgroundColor: isFirstNews ? 'rgba(255,255,255,0.05)' : 'rgba(255,255,255,0.1)' 
                } 
            }}
        >
          <ArrowBackIosNewIcon />
        </IconButton>
        <IconButton 
            onClick={handleNextCallback} 
            aria-label="nächste nachricht"
            disabled={isLastNews} // Deaktiviert, wenn letzte Nachricht
            sx={{ 
                color: isLastNews ? 'grey.700' : 'grey.400', // Ausgegraut, wenn deaktiviert
                backgroundColor: 'rgba(255,255,255,0.05)',
                '&:hover': { 
                    backgroundColor: isLastNews ? 'rgba(255,255,255,0.05)' : 'rgba(255,255,255,0.1)' 
                } 
            }}
        >
          <ArrowForwardIosIcon />
        </IconButton>
      </Box>
    </Container>
  );
};

export default NewsCarousel;
