import * as React from 'react';
import {
  Box,
  Container,
  Typography,
  TextField,
  Button,
  Grid,
  Paper,
  MenuItem,
  Select,
  FormControl,
  InputLabel,
  useTheme,
  TableContainer, 
  Table,
  TableHead,
  TableBody,
  TableRow,
  TableCell,
  ListItemIcon,
} from '@mui/material';
import SendIcon from '@mui/icons-material/Send';
import BusinessIcon from '@mui/icons-material/Business';
import AccountBalanceWalletIcon from '@mui/icons-material/AccountBalanceWallet';
import SportsSoccerIcon from '@mui/icons-material/SportsSoccer';
import LocationOnIcon from '@mui/icons-material/LocationOn';
import AssignmentIcon from '@mui/icons-material/Assignment';
import EventSeatIcon from '@mui/icons-material/EventSeat';
import GrassIcon from '@mui/icons-material/Grass';
import AccountBalanceIcon from '@mui/icons-material/AccountBalance';
import QrCode2Icon from '@mui/icons-material/QrCode2';
import PeopleIcon from '@mui/icons-material/People'; 

// Farben
const blaugrün = '#00A99D';
const gelb = '#FFBF00';

// Daten für die Kontaktseite
const contactInfo = {
  generalNote: "Kontaktadressen der einzelnen Teams findet Ihr auf der Seite Teams! Für andere Anliegen nutze bitte dieses Formular.",
  address: {
    name: "Bunte Liga Freiburg",
    person: "c/o Jonas Krause",
    street: "Ferdinand-Weiß-Straße 92",
    city: "79106 Freiburg",
  },
  responsibilities: [
    { function: "1. Vorstand", name: "Jonas Krause", icon: <BusinessIcon sx={{ color: gelb }} /> },
    { function: "2. Vorstand", name: "Jens Karsten", icon: <BusinessIcon sx={{ color: gelb }} /> },
    { function: "Kassenwart", name: "Peter Deutschmann", icon: <AccountBalanceWalletIcon sx={{ color: gelb }} /> },
    { function: "Kassenprüfer", name: "Klaus Becker + Valentin Czisch", icon: <PeopleIcon sx={{ color: gelb }} /> },
    { function: "Spielausschuss", name: "Benedikt Greiner (LokLit) und Dominik Seifer (Dynamo Tresen)", icon: <SportsSoccerIcon sx={{ color: gelb }} /> },
    { function: "Ergebnisdienst", name: "Peter", icon: <AssignmentIcon sx={{ color: gelb }} /> },
    { function: "Platzreservierung", name: "Carlos", icon: <EventSeatIcon sx={{ color: gelb }} /> },
    { function: "Platzpflege", name: "Rasenteam", icon: <GrassIcon sx={{ color: gelb }} /> },
  ],
  bankDetails: {
    name: "Sparkasse Freiburg",
    iban: "DE06 6805 0101 0010 1030 69",
    bic: "FRSPDE66XXX",
    qrNote: "Oder zahl einfach per QR Code."
  }
};


const ContactSection = ({ title, icon, children, sx }) => {
    const theme = useTheme();
    return (
        <Paper
            elevation={3}
            sx={{
                p: { xs: 2, sm: 2.5, md: 3 }, // Responsive Padding
                mb: 4, // Einheitlicher Abstand nach unten
                backgroundColor: 'rgba(25, 25, 25, 0.85)', // Etwas heller als Hintergrund für Abhebung
                backdropFilter: 'blur(5px)',
                borderRadius: theme.shape.borderRadius,
                border: `1px solid ${theme.palette.grey[800]}`,
                width: '100%', 
                ...sx,
            }}
        >
            {title && (
              <Box sx={{ display: 'flex', alignItems: 'center', mb: 2 }}> {/* Mehr Abstand zum Inhalt */}
                  {icon && <ListItemIcon sx={{ minWidth: 36, color: gelb, mr:1.5 }}>{icon}</ListItemIcon>}
                  <Typography variant="h5" component="h3" sx={{ fontFamily: 'comfortaa', fontWeight: 600, color: theme.palette.common.white }}>
                      {title}
                  </Typography>
              </Box>
            )}
            {children}
        </Paper>
    );
};


const ContactPage = () => {
  const theme = useTheme();
  const [formData, setFormData] = React.useState({
    recipient: '',
    name: '',
    email: '',
    subject: '',
    message: '',
  });

  const handleChange = (event) => {
    const { name, value } = event.target;
    setFormData((prevData) => ({
      ...prevData,
      [name]: value,
    }));
  };

  const handleSubmit = (event) => {
    event.preventDefault();
    console.log('Formulardaten:', formData);
    setFormData({ recipient: '', name: '', email: '', subject: '', message: '' });
    alert('Nachricht gesendet (simuliert)!');
  };

  const inputLabelStyles = {
    color: theme.palette.grey[300], // Helles Grau für Labels
    fontFamily: 'comfortaa',
    '&.Mui-focused': {
      color: gelb, 
    },
     // Style für das Label, wenn es "geschrumpft" ist (oben)
    '&.MuiInputLabel-shrink': {
        color: theme.palette.grey[400], // Etwas dunkler, wenn oben
         '&.Mui-focused': {
            color: gelb, // Akzentfarbe, wenn fokussiert und oben
        },
    }
  };

  const formFieldBaseStyles = {
    width: '100%', // Stellt sicher, dass das Feld die volle Breite des Grid-Items nimmt
    '& .MuiInputBase-input': {
      color: theme.palette.common.white, 
      fontFamily: 'comfortaa',
      fontSize: '0.95rem', // Leicht größere Schrift in Feldern
    },
    '& .MuiOutlinedInput-root': {
      backgroundColor: 'rgba(255,255,255,0.05)', 
      '& fieldset': {
        borderColor: theme.palette.grey[700],
      },
      '&:hover fieldset': {
        borderColor: theme.palette.grey[500],
      },
      '&.Mui-focused fieldset': {
        borderColor: gelb, 
      },
    },
    '& .MuiInputLabel-root': inputLabelStyles,
    '& .MuiInputLabel-formControl:not(.Mui-focused):not(.MuiFormLabel-filled)': {
        color: theme.palette.grey[500], 
    },
  };
  
  const selectStyles = {
    ...formFieldBaseStyles,
    '& .MuiSelect-select': {
        backgroundColor: 'transparent',
        color: theme.palette.common.white, 
        paddingTop: '14px', // Anpassung für bessere vertikale Zentrierung des Textes
        paddingBottom: '14px',
        lineHeight: '1.4375em', // Standard MUI Wert
    },
     '& .MuiSvgIcon-root': { 
        color: theme.palette.grey[400],
    }
  };


  return (
    // Hauptcontainer der Seite
    <Container maxWidth="md" sx={{ py: 4, color: theme.palette.common.white }}> {/* maxWidth="md" für bessere Lesbarkeit */}
      <Typography
        variant="h3"
        component="h1"
        gutterBottom
        textAlign="center"
        sx={{ fontFamily: 'comfortaa', fontWeight: 700, color: blaugrün, mb: 3 }}
      >
        KONTAKT
      </Typography>

      <Typography
        variant="body1"
        textAlign="center"
        sx={{ fontFamily: 'comfortaa', color: theme.palette.grey[300], mb: 5 }}
      >
        {contactInfo.generalNote}
      </Typography>

      {/* Grid-Container für alle Sektionen, erzwingt vertikale Anordnung */}
      <Grid container spacing={0} direction="column" alignItems="center">
        
        <Grid item xs={12} sx={{width: '100%', mb:4}}> 
          <ContactSection title="Anschrift" icon={<LocationOnIcon />}>
            <Typography variant="body1" sx={{ fontFamily: 'comfortaa', lineHeight: 1.7, color: theme.palette.grey[200] }}>
              {contactInfo.address.name}<br />
              {contactInfo.address.person}<br />
              {contactInfo.address.street}<br />
              {contactInfo.address.city}
            </Typography>
          </ContactSection>
        </Grid>

        <Grid item xs={12} sx={{width: '100%', mb:4}}> 
            <ContactSection title="Verantwortliche Personen" icon={<BusinessIcon />}>
                <TableContainer>
                    <Table size="small" aria-label="Verantwortliche Personen">
                        <TableHead>
                            <TableRow sx={{ '& .MuiTableCell-head': { color: theme.palette.common.white, fontWeight:'bold', fontFamily:'comfortaa', borderBottom: `1px solid ${theme.palette.grey[700]}`, fontSize: '0.9rem', py: 1 }}}>
                                <TableCell sx={{width: {xs: '45%', sm:'40%'}, py:1.5}}>Funktion</TableCell>
                                <TableCell sx={{py:1.5}}>Name(n)</TableCell>
                            </TableRow>
                        </TableHead>
                        <TableBody>
                            {contactInfo.responsibilities.map((item) => (
                                <TableRow key={item.function} sx={{ '&:last-child td, &:last-child th': { border: 0 } }}>
                                    <TableCell component="th" scope="row" sx={{color: theme.palette.grey[100], fontFamily:'comfortaa', borderBottom: `1px solid ${theme.palette.grey[800]}`, py:1.5}}>
                                        <Box sx={{display: 'flex', alignItems: 'center'}}>
                                            {item.icon && <ListItemIcon sx={{minWidth: 'auto', mr: 1.5 }}>{React.cloneElement(item.icon, { sx: { color: gelb, fontSize: '1.4rem' }})}</ListItemIcon>}
                                            <Typography variant="body1" sx={{fontFamily:'comfortaa', color: 'inherit', fontSize:'0.9rem'}}>
                                                {item.function}
                                            </Typography>
                                        </Box>
                                    </TableCell>
                                    <TableCell sx={{color: theme.palette.grey[200], fontFamily:'comfortaa', borderBottom: `1px solid ${theme.palette.grey[800]}`, py:1.5}}>
                                        <Typography variant="body1" sx={{fontFamily:'comfortaa', color: 'inherit', fontSize:'0.9rem'}}>
                                            {item.name}
                                        </Typography>
                                    </TableCell>
                                </TableRow>
                            ))}
                        </TableBody>
                    </Table>
                </TableContainer>
            </ContactSection>
        </Grid>
        
        <Grid item xs={12} sx={{width: '100%', mb:4}}> 
          <ContactSection title="Bankverbindung" icon={<AccountBalanceIcon />}>
            <Typography variant="body1" sx={{fontFamily: 'comfortaa', lineHeight: 1.7, color: theme.palette.grey[200]}}>
                {contactInfo.bankDetails.name}<br/>
                IBAN: {contactInfo.bankDetails.iban}<br/>
                BIC: {contactInfo.bankDetails.bic}
            </Typography>
            <Typography variant="caption" sx={{fontFamily: 'comfortaa', color: theme.palette.grey[400], display:'flex', alignItems:'center', mt:1}}>
               <QrCode2Icon sx={{fontSize: '1.2rem', mr:0.5, color: gelb}}/> {contactInfo.bankDetails.qrNote}
            </Typography>
          </ContactSection>
        </Grid>

        <Grid item xs={12} sx={{width: '100%'}}> 
           <Typography
            variant="h5"
            component="h2"
            textAlign="center"
            sx={{
              fontFamily: 'comfortaa',
              fontWeight: 600,
              color: theme.palette.common.white,
              mb: 2,
              borderBottom: `2px solid ${gelb}`,
              pb: 1,
              display: 'inline-block', // Damit borderBottom nur so breit wie Text ist
              position: 'relative', // Für Zentrierung mit transform
              left: '50%',
              transform: 'translateX(-50%)',
            }}
          >
            Schreib uns!
          </Typography>
          <Paper
            elevation={3}
            sx={{
              p: {xs: 2, sm:3, md: 4}, // Mehr Padding im Formular
              backgroundColor: 'rgba(25, 25, 25, 0.85)',
              backdropFilter: 'blur(5px)',
              borderRadius: theme.shape.borderRadius,
              border: `1px solid ${theme.palette.grey[800]}`,
            }}
          >
            <Box component="form" onSubmit={handleSubmit} noValidate>
              <Grid container spacing={3}> {/* Etwas mehr Abstand zwischen Feldern */}
                <Grid item xs={12}>
                  <FormControl fullWidth sx={selectStyles}>
                    <InputLabel id="recipient-label">An wen?</InputLabel>
                    <Select
                      labelId="recipient-label"
                      id="recipient"
                      name="recipient"
                      value={formData.recipient}
                      label="An wen?"
                      onChange={handleChange}
                      MenuProps={{
                        PaperProps: {
                          sx: {
                            backgroundColor: 'rgb(20,20,20)', 
                            color: theme.palette.common.white,
                            fontFamily: 'comfortaa',
                            border: `1px solid ${theme.palette.grey[700]}`,
                            '& .MuiMenuItem-root': {
                                fontFamily: 'comfortaa',
                                color: theme.palette.grey[300], 
                                '&:hover': {
                                    backgroundColor: theme.palette.grey[800],
                                    color: theme.palette.common.white,
                                },
                                '&.Mui-selected': { 
                                    backgroundColor: 'rgba(255, 191, 0, 0.2)', // Etwas stärkerer Gelb-Akzent
                                    color: theme.palette.common.white,
                                    '&:hover': {
                                       backgroundColor: 'rgba(255, 191, 0, 0.3)',
                                    }
                                }
                            }
                          },
                        },
                      }}
                    >
                      <MenuItem value="" disabled sx={{fontFamily:'comfortaa'}}><em>Bitte auswählen</em></MenuItem>
                      <MenuItem value="vorstand" sx={{fontFamily:'comfortaa'}}>Vorstand</MenuItem>
                      <MenuItem value="kassenwart" sx={{fontFamily:'comfortaa'}}>Kassenwart</MenuItem>
                      <MenuItem value="spielausschuss" sx={{fontFamily:'comfortaa'}}>Spielausschuss</MenuItem>
                      <MenuItem value="ergebnisdienst" sx={{fontFamily:'comfortaa'}}>Ergebnisdienst</MenuItem>
                      <MenuItem value="platzreservierung" sx={{fontFamily:'comfortaa'}}>Platzreservierung/Platzpflege</MenuItem>
                      <MenuItem value="sonstiges" sx={{fontFamily:'comfortaa'}}>Sonstiges</MenuItem>
                    </Select>
                  </FormControl>
                </Grid>
                <Grid item xs={12}>
                  <TextField
                    required
                    fullWidth
                    id="name"
                    label="Dein Name"
                    name="name"
                    value={formData.name}
                    onChange={handleChange}
                    sx={formFieldBaseStyles}
                  />
                </Grid>
                <Grid item xs={12}>
                  <TextField
                    required
                    fullWidth
                    id="email"
                    label="Deine E-Mail"
                    name="email"
                    type="email"
                    value={formData.email}
                    onChange={handleChange}
                    sx={formFieldBaseStyles}
                  />
                </Grid>
                <Grid item xs={12}>
                  <TextField
                    required
                    fullWidth
                    id="subject"
                    label="Betreff"
                    name="subject"
                    value={formData.subject}
                    onChange={handleChange}
                    sx={formFieldBaseStyles}
                  />
                </Grid>
                <Grid item xs={12}>
                  <TextField
                    required
                    fullWidth
                    id="message"
                    label="Deine Nachricht"
                    name="message"
                    multiline
                    rows={6} // Etwas mehr Platz für die Nachricht
                    value={formData.message}
                    onChange={handleChange}
                    sx={formFieldBaseStyles}
                  />
                </Grid>
                <Grid item xs={12}>
                  <Button
                    type="submit"
                    fullWidth
                    variant="contained"
                    endIcon={<SendIcon />}
                    sx={{
                      fontFamily: 'comfortaa',
                      fontWeight: 'bold',
                      py: 1.5,
                      fontSize: '1rem', // Größere Schrift für Button
                      backgroundColor: gelb, 
                      color: theme.palette.common.black, 
                      '&:hover': {
                        backgroundColor: theme.palette.warning.dark, 
                      },
                    }}
                  >
                    Nachricht senden
                  </Button>
                </Grid>
              </Grid>
            </Box>
          </Paper>
        </Grid>
      </Grid>
    </Container>
  );
};

export default ContactPage;
