import * as React from 'react';
import {
  Table,
  TableBody,
  TableCell,
  TableContainer,
  TableHead,
  TableRow,
  Container,
  Paper,
  Box,
  Typography,
  Avatar,
  useTheme,
} from '@mui/material';

// Beispieldaten für 19 Teams
const createTeamData = (
  rank,
  logo,
  name,
  played,
  won,
  drawn,
  lost,
  goalsFor,
  goalsAgainst,
  points
) => {
  const goalDifference = goalsFor - goalsAgainst;
  return {
    rank,
    logo,
    name,
    played,
    won,
    drawn,
    lost,
    goalsFor,
    goalsAgainst,
    goalDifference,
    points,
  };
};

const tableData = [
  createTeamData(1, 'logo_fc_ferdi.png', '1. FC Ferdi Weiß', 18, 15, 2, 1, 55, 12, 47),
  createTeamData(2, 'logo_dynamo_tresen.png', 'Dynamo Tresen', 18, 14, 1, 3, 48, 15, 43),
  createTeamData(3, 'logo_sc_hintertux.png', 'SC Hintertux', 18, 12, 3, 3, 35, 18, 39),
  createTeamData(4, 'logo_fc_saufhemden.png', 'FC Saufhemden', 18, 11, 2, 5, 40, 25, 35),
  createTeamData(5, 'logo_sv_ackerfurche.png', 'SV Ackerfurche', 18, 10, 4, 4, 33, 20, 34),
  createTeamData(6, 'logo_vfb_halbstark.png', 'VfB Halbstark', 18, 9, 3, 6, 30, 28, 30),
  createTeamData(7, 'logo_fc_thekenproleten.png', 'FC Thekenproleten', 18, 8, 5, 5, 29, 29, 29),
  createTeamData(8, 'logo_sg_sonnenstich.png', 'SG Sonnenstich', 18, 7, 4, 7, 25, 27, 25),
  createTeamData(9, 'logo_fc_beton.png', 'FC Beton', 18, 6, 6, 6, 22, 22, 24),
  createTeamData(10, 'logo_as_tralkörper.png', 'AS Tralkörper', 18, 5, 7, 6, 28, 31, 22),
  createTeamData(11, 'logo_fc_abseitsfalle.png', 'FC Abseitsfalle', 18, 6, 3, 9, 20, 30, 21),
  createTeamData(12, 'logo_tsv_thekenbolzer.png', 'TSV Thekenbolzer', 18, 5, 4, 9, 18, 33, 19),
  createTeamData(13, 'logo_fc_rumpel.png', 'FC Rumpel', 18, 4, 5, 9, 15, 28, 17),
  createTeamData(14, 'logo_spvgg_kickandrush.png', 'SpVgg Kick&Rush', 18, 4, 3, 11, 19, 38, 15),
  createTeamData(15, 'logo_fc_lattenknaller.png', 'FC Lattenknaller', 18, 3, 5, 10, 17, 35, 14),
  createTeamData(16, 'logo_sv_platzwart.png', 'SV Platzwart', 18, 3, 4, 11, 14, 32, 13),
  createTeamData(17, 'logo_fc_dauerstramm.png', 'FC Dauerstramm', 18, 2, 6, 10, 12, 30, 12),
  createTeamData(18, 'logo_die_optimisten.png', 'Die Optimisten', 18, 2, 4, 12, 10, 40, 10),
  createTeamData(19, 'logo_fc_hoffnungslos.png', 'FC Hoffnungslos', 18, 1, 2, 15, 8, 50, 5),
];

// Stil für die Tabellenzellen
const StyledTableCell = ({ children, sx, ...props }) => {
  const theme = useTheme();
  return (
    <TableCell
      sx={{
        color: theme.palette.grey[300],
        fontFamily: 'comfortaa',
        borderBottom: `1px solid ${theme.palette.grey[800]}`,
        py: 1.5, // Vertikales Padding
        ...sx,
      }}
      {...props}
    >
      {children}
    </TableCell>
  );
};

const LeagueTable = () => {
  const theme = useTheme();

  return (
    <Container maxWidth="xl" sx={{ my: 4 }}>
      <Typography 
        variant='h4'
        sx={{ 
            mb: 3,
            mt: 2,
            color: theme.palette.error.main, 
            fontWeight: 700,
            fontFamily: 'comfortaa',
            textAlign: 'center',
            textTransform: 'uppercase',
            letterSpacing: '0.1em',
        }}
      >
        LIGA-TABELLE
      </Typography>
      <TableContainer 
        component={Paper} 
        sx={{ 
            backgroundColor: '#111', // Dunkler Hintergrund für den Container
            borderRadius: theme.shape.borderRadius,
            border: `1px solid ${theme.palette.grey[800]}`,
        }}
      >
        <Table aria-label="Fußballtabelle">
          <TableHead>
            <TableRow sx={{ backgroundColor: 'rgba(255,255,255,0.05)'}}>
              <StyledTableCell sx={{ fontWeight: 'bold', color: theme.palette.grey[100], width: '5%' }}>#</StyledTableCell>
              <StyledTableCell sx={{ fontWeight: 'bold', color: theme.palette.grey[100], width: '30%' }}>Team</StyledTableCell>
              <StyledTableCell align="center" sx={{ fontWeight: 'bold', color: theme.palette.grey[100] }}>Sp.</StyledTableCell>
              <StyledTableCell align="center" sx={{ fontWeight: 'bold', color: theme.palette.grey[100] }}>S</StyledTableCell>
              <StyledTableCell align="center" sx={{ fontWeight: 'bold', color: theme.palette.grey[100] }}>U</StyledTableCell>
              <StyledTableCell align="center" sx={{ fontWeight: 'bold', color: theme.palette.grey[100] }}>N</StyledTableCell>
              <StyledTableCell align="center" sx={{ fontWeight: 'bold', color: theme.palette.grey[100] }}>Tore</StyledTableCell>
              <StyledTableCell align="center" sx={{ fontWeight: 'bold', color: theme.palette.grey[100] }}>Diff.</StyledTableCell>
              <StyledTableCell align="center" sx={{ fontWeight: 'bold', color: theme.palette.grey[100] }}>Pkt.</StyledTableCell>
            </TableRow>
          </TableHead>
          <TableBody>
            {tableData.map((row, index) => (
              <TableRow 
                key={row.name} 
                sx={{ 
                    '&:hover': { backgroundColor: 'rgba(255,255,255,0.08)' },
                    // Unterschiedliche Hintergrundfarben für die ersten Plätze (optional)
                    ...(row.rank === 1 && { backgroundColor: 'rgba(229, 115, 115, 0.1)'}), // Leichter Rotton für Platz 1
                    ...(row.rank >= 2 && row.rank <=3 && { backgroundColor: 'rgba(100, 100, 100, 0.1)'}), // Leichter Grauton für Plätze 2-3
                }}
              >
                <StyledTableCell component="th" scope="row" sx={{ fontWeight: 'bold', color: row.rank === 1 ? theme.palette.error.light : theme.palette.grey[100]}}>
                  {row.rank}
                </StyledTableCell>
                <StyledTableCell>
                  <Box sx={{ display: 'flex', alignItems: 'center' }}>
                    <Avatar 
                        src={`https://placehold.co/30x30/1a1a1a/FFFFFF?text=${row.name.substring(0,1)}&font=comfortaa`} // Placeholder mit Initialen
                        alt={`${row.name} Logo`} 
                        sx={{ 
                            width: 24, 
                            height: 24, 
                            mr: 1.5, 
                            fontSize: '0.8rem',
                            backgroundColor: theme.palette.grey[700] // Hintergrund für Avatar
                        }} 
                        imgProps={{onError: (e) => { e.target.src = `https://placehold.co/30x30/1a1a1a/FFFFFF?text=${row.name.substring(0,1)}&font=comfortaa`; e.target.alt = 'Logo nicht geladen'; } }}
                    />
                    <Typography variant="body2" sx={{ fontFamily: 'comfortaa', color: theme.palette.grey[100] }}>
                        {row.name}
                    </Typography>
                  </Box>
                </StyledTableCell>
                <StyledTableCell align="center">{row.played}</StyledTableCell>
                <StyledTableCell align="center">{row.won}</StyledTableCell>
                <StyledTableCell align="center">{row.drawn}</StyledTableCell>
                <StyledTableCell align="center">{row.lost}</StyledTableCell>
                <StyledTableCell align="center">{`${row.goalsFor}:${row.goalsAgainst}`}</StyledTableCell>
                <StyledTableCell align="center" sx={{ fontWeight: 'bold', color: row.goalDifference > 0 ? theme.palette.success.light : (row.goalDifference < 0 ? theme.palette.error.light : theme.palette.grey[300]) }}>
                    {row.goalDifference > 0 ? `+${row.goalDifference}` : row.goalDifference}
                </StyledTableCell>
                <StyledTableCell align="center" sx={{ fontWeight: 'bold', color: theme.palette.grey[100] }}>{row.points}</StyledTableCell>
              </TableRow>
            ))}
          </TableBody>
        </Table>
      </TableContainer>
    </Container>
  );
};

export default LeagueTable;
