import React from 'react';
import { Box, Container, Typography, Link, IconButton, Divider } from '@mui/material';
import { useTheme } from '@mui/material/styles';

// Importiere die Icons, die du verwenden möchtest
import FacebookIcon from '@mui/icons-material/Facebook';
import InstagramIcon from '@mui/icons-material/Instagram';
import TwitterIcon from '@mui/icons-material/Twitter';
import MailOutlineIcon from '@mui/icons-material/MailOutline';

const Footer = () => {
  const theme = useTheme();

  // Dieselben Style-Variablen wie in deinem Header für ein konsistentes Design
  const footerBackgroundColor = 'rgba(0, 0, 0, 0.85)';
  const footerBlur = 'blur(8px)';

  return (
    <Box
      component="footer"
      sx={{
        backgroundColor: '#000', // Solider schwarzer Hintergrund wie bei der AppBar
        backdropFilter: footerBlur,
        color: theme.palette.grey[500], // Leicht gräulicher Text für subtilen Kontrast
        py: { xs: 3, md: 4 }, // Vertikales Padding, mehr auf Desktop
        mt: 'auto', // Wichtig: Schiebt den Footer nach unten, wenn der Seiteninhalt kurz ist
        borderTop: `1px solid ${theme.palette.grey[800]}`, // Feine obere Trennlinie
      }}
    >
      <Container maxWidth="xl">
        <Box sx={{ display: 'flex', flexDirection: 'column', alignItems: 'center', textAlign: 'center' }}>
          <Box 
            sx={{ 
              display: 'flex', 
              gap: { xs: 2, md: 3 }, 
              mb: 2, 
              flexWrap: 'wrap', 
              justifyContent: 'center',
              fontFamily: 'comfortaa',
              fontWeight: 400
            }}
          >
            <Link href="/kontakt" color="inherit" underline="hover">Kontakt</Link>
            <Link href="/impressum" color="inherit" underline="hover">Impressum</Link>
            <Link href="/datenschutz" color="inherit" underline="hover">Datenschutz</Link>
          </Box>
          
          <Divider sx={{ width: '50%', maxWidth: '300px', borderColor: 'rgba(255, 255, 255, 0.1)', mb: 2 }} />

          {/* Copyright */}
          <Typography variant="body2" sx={{ fontFamily: 'comfortaa', color: theme.palette.grey[400] }}>
            © {new Date().getFullYear()} HFT Rakete Freiburg
          </Typography>
        </Box>
      </Container>
    </Box>
  );
};

export default Footer;
