import React from 'react';
import { useLocation } from 'react-router-dom'; 
import NewsCarousel from '../components/News/News';
import LeagueTable from '../components/LeagueTable';
import FixtureList from '../components/FixtureList';
// import './HomePage.css'; // Erstelle HomePage.css für spezifische Styles

const HomePage = () => {
  const location = useLocation(); // Aktuelle Location-Informationen abrufen

  React.useEffect(() => {
    const attemptScroll = () => {
      if (location.hash) {
        const id = location.hash.replace('#', '');
        const element = document.getElementById(id);
        if (element) {
          // Stelle sicher, dass das Element einen scroll-margin-top hat,
          // um Platz für den Sticky Header zu lassen.
          // Dies wird am besten über CSS für das Element mit der ID "Ergebnisse" gemacht.
          // Beispiel: #Ergebnisse { scroll-margin-top: 90px; } (oder die Höhe deines Headers)
          element.scrollIntoView({ behavior: 'smooth', block: 'start' });
        }
      }
    };

    // Ein kleiner Timeout kann helfen, sicherzustellen, dass das Element gerendert ist,
    // besonders wenn man sich bereits auf der Seite befindet und nur der Hash sich ändert.
    const timeoutId = setTimeout(attemptScroll, 50); 

    return () => clearTimeout(timeoutId); // Aufräumen des Timeouts

  }, [location]); // Effekt erneut ausführen, wenn sich das gesamte location-Objekt ändert

  return (
    <div className="homepage">
      <NewsCarousel />
      <LeagueTable title="Liga-Tabelle" form={true}/>
      
      {/* WICHTIG: Füge einen CSS-Style für diese Sektion hinzu, um den Sticky Header zu berücksichtigen.
        Beispiel in deiner globalen CSS-Datei oder einer .css-Datei für HomePage:
        #Ergebnisse {
          scroll-margin-top: 90px; // Passe diesen Wert an die Höhe deiner AppBar an (im nicht-geschrumpften Zustand)
        }
        Alternativ direkt als Inline-Style (weniger empfohlen für globale Styles):
        <section id="Ergebnisse" style={{ scrollMarginTop: '90px' }}> 
      */}
      <section id="Ergebnisse"> 
        <FixtureList />
      </section>
      
      {/* <section id="Tabelle" style={{ scrollMarginTop: '90px' }}>
        <LeagueTable title="Liga-Tabelle" form={true} />
      </section>
      <section id="News" style={{ scrollMarginTop: '90px' }}>
        <NewsCarousel />
      </section> 
      */}
    </div>
  );
};

export default HomePage;
