import * as React from 'react';
import {
  Table,
  TableBody,
  TableCell,
  TableContainer,
  TableHead,
  TableRow,
  Paper,
  Box,
  Typography,
  Avatar,
  useTheme,
  Container,
  useMediaQuery,
  Chip,
} from '@mui/material';

// Hilfsfunktion zum Erstellen der Spieldaten
const createFixtureData = (
  id,
  date, // z.B. "08.06.2025"
  time, // z.B. "15:00"
  homeTeam,
  homeLogoColor,
  awayTeam,
  awayLogoColor,
  homeScore, // null für zukünftige Spiele
  awayScore, // null für zukünftige Spiele
  location,
  isPast // boolean
) => {
  return {
    id,
    date,
    time,
    homeTeam,
    homeLogoColor,
    awayTeam,
    awayLogoColor,
    homeScore,
    awayScore,
    location,
    isPast,
  };
};

// Beispieldaten für Spiele
const initialFixtureData = [
  createFixtureData(1, '01.06.2025', '14:00', 'Rakete Freiburg', '#D32F2F', 'Die 2. Wahl', '#757575', 3, 1, 'Sportplatz Mitte', true),
  createFixtureData(2, '01.06.2025', '16:00', 'Lokomotive Littenweiler', '#5D4037', 'Red Sea', '#C2185B', 2, 2, 'Bolzplatz West', true),
  createFixtureData(3, '08.06.2025', '15:00', 'Wurschtwecklebombers', '#00796B', 'Latinos Freiburg', '#FBC02D', null, null, 'Stadion Rote Erde', false),
  createFixtureData(4, '08.06.2025', '17:00', 'Soccerfield Rockers', '#388E3C', 'Dynamo Tresen', '#1976D2', 1, 0, 'Kunstrasen Nord', true),
  createFixtureData(5, '15.06.2025', '14:00', 'Kante Freiburg', '#E91E63', 'Union Eisenwaden', '#00BCD4', null, null, 'Sportplatz Ost', false),
  createFixtureData(6, '15.06.2025', '16:00', 'Haxe des Bösen', '#B71C1C', 'SEK', '#424242', 0, 4, 'Ascheplatz Süd', true),
  createFixtureData(7, '22.06.2025', '13:00', 'Chimichurri Auriazul', '#64B5F6', 'Kämpfende Herzen', '#F5F5F5', null, null, 'Sportplatz Mitte', false),
  createFixtureData(8, '22.06.2025', '15:00', '1. FC Ferdi Weiss', '#81D4FA', 'Dampflokomotive Littenweiler', '#795548', 5, 0, 'Bolzplatz West', true),
  createFixtureData(9, '29.06.2025', '14:00', 'Zeugen Yeboahs', '#FFEB3B', 'Dübel-Lattenkreuz', '#D7CCC8', null, null, 'Stadion Rote Erde', false),
  createFixtureData(10, '29.06.2025', '16:00', 'CC Freiburg', '#212121', 'Rakete Freiburg', '#D32F2F', 1, 1, 'Kunstrasen Nord', true),
];

// Stil für die Tabellenzellen
const StyledTableCell = ({ children, sx, align, hideOnMobile, ...props }) => {
  const theme = useTheme();
  const isMobile = useMediaQuery(theme.breakpoints.down('sm'));

  if (isMobile && hideOnMobile) {
    return null;
  }

  return (
    <TableCell
      align={align}
      sx={{
        color: theme.palette.grey[300],
        fontFamily: 'comfortaa',
        borderBottom: `1px solid ${theme.palette.grey[800]}`,
        py: isMobile ? 0.6 : 1.2, 
        px: isMobile ? 0.4 : 1, 
        fontSize: isMobile ? '0.65rem' : '0.85rem', 
        // Standardmäßig vertikal zentrieren, wenn nicht anders angegeben
        verticalAlign: 'middle', 
        ...sx,
      }}
      {...props}
    >
      {children}
    </TableCell>
  );
};

const FixtureList = () => {
  const theme = useTheme();
  const isMobile = useMediaQuery(theme.breakpoints.down('sm'));

  // Funktion zum Parsen des Datums für die Sortierung
  const parseDate = (dateString) => {
    const [day, month, year] = dateString.split('.');
    return new Date(`${year}-${month}-${day}`);
  };

  // Sortiere die Spieldaten absteigend nach Datum
  const sortedFixtureData = React.useMemo(() => {
    return [...initialFixtureData].sort((a, b) => parseDate(b.date) - parseDate(a.date));
  }, []); // Abhängigkeit ist leer, da initialFixtureData als konstant angenommen wird

  return (
    <Container maxWidth="xl" sx={{ my: 4, px: isMobile ? 0.5 : 2 }}>
      <Typography 
        variant={isMobile ? 'h6' : 'h4'}
        sx={{ 
            mb: isMobile ? 2 : 3,
            mt: 2,
            color: theme.palette.error.main, 
            fontWeight: 700,
            fontFamily: 'comfortaa',
            textAlign: 'center',
            textTransform: 'uppercase',
            letterSpacing: '0.1em',
        }}
      >
        SPIELPLAN
      </Typography>
      <TableContainer 
        component={Paper} 
        sx={{ 
            backgroundColor: '#111', 
            borderRadius: theme.shape.borderRadius,
            border: `1px solid ${theme.palette.grey[800]}`,
        }}
      >
        <Table aria-label="Spielplan Tabelle" size="small">
          <TableHead>
            <TableRow sx={{ backgroundColor: 'rgba(255,255,255,0.05)'}}>
              <StyledTableCell sx={{ fontWeight: 'bold', color: theme.palette.grey[100], width: isMobile ? '22%' : '15%' }}>Datum</StyledTableCell>
              <StyledTableCell align="center" sx={{ fontWeight: 'bold', color: theme.palette.grey[100], width: isMobile ? '13%' : '10%' }}>Uhrzeit</StyledTableCell>
              <StyledTableCell sx={{ fontWeight: 'bold', color: theme.palette.grey[100], width: isMobile ? '25%' : '25%', textAlign: isMobile ? 'center': 'left' }}>Heim</StyledTableCell>
              <StyledTableCell align="center" sx={{ fontWeight: 'bold', color: theme.palette.grey[100], width: '10%' }}>Erg.</StyledTableCell>
              <StyledTableCell sx={{ fontWeight: 'bold', color: theme.palette.grey[100], width: isMobile ? '25%' : '25%', textAlign: isMobile ? 'center': 'left' }}>Auswärts</StyledTableCell>
              <StyledTableCell sx={{ fontWeight: 'bold', color: theme.palette.grey[100], width: '15%' }} hideOnMobile={true}>Ort</StyledTableCell>
            </TableRow>
          </TableHead>
          <TableBody>
            {sortedFixtureData.map((row) => (
              <TableRow 
                key={row.id} 
                sx={{ 
                    '&:hover': { backgroundColor: 'rgba(255,255,255,0.08)' },
                    opacity: row.isPast ? 0.7 : 1, 
                }}
              >
                <StyledTableCell>
                    <Box sx={{display: 'flex', flexDirection:'column', alignItems: isMobile? 'center' : 'flex-start', textAlign: isMobile? 'center' : 'left'}}>
                        <Typography variant="caption" sx={{ fontFamily: 'comfortaa', color: theme.palette.grey[400], fontSize: isMobile? '0.55rem':'0.7rem' }}>
                            {new Date(row.date.split('.').reverse().join('-')).toLocaleDateString('de-DE', { weekday: 'short' })}
                        </Typography>
                        {row.date}
                    </Box>
                </StyledTableCell>
                <StyledTableCell align="center">{row.time}</StyledTableCell>
                {/* Die sx-Prop hier wurde vereinfacht, um die Standard-Zentrierung zu nutzen */}
                <StyledTableCell sx={{ pt: isMobile ? 0.5 : undefined /* Explizites pt nur für Mobile, wenn nötig */ }}>
                  <Box sx={{ 
                      display: 'flex', 
                      flexDirection: isMobile ? 'column' : 'row', 
                      alignItems: 'center', 
                      justifyContent: isMobile ? 'center' : 'flex-start',
                      textAlign: isMobile ? 'center' : 'left',
                    }}
                  >
                    <Avatar 
                        alt={`${row.homeTeam} Logo`} 
                        sx={{ 
                            width: isMobile ? 20 : 20, 
                            height: isMobile ? 24 : 20, 
                            mb: isMobile ? 0.5 : 0, 
                            mr: isMobile ? 0 : 1, 
                            fontSize: isMobile ? '0.8rem' : '0.7rem',
                            color: theme.palette.getContrastText(row.homeLogoColor || theme.palette.grey[700]), 
                            backgroundColor: row.homeLogoColor || theme.palette.grey[700] 
                        }} 
                    >
                        {row.homeTeam.substring(0,1).toUpperCase()}
                    </Avatar>
                    <Typography variant="body2" sx={{ fontFamily: 'comfortaa', color: theme.palette.grey[100], fontSize: isMobile ? '0.65rem' : '0.8rem', whiteSpace: isMobile? 'normal' : 'nowrap', overflow: 'hidden', textOverflow: 'ellipsis' }}>
                        {row.homeTeam}
                    </Typography>
                  </Box>
                </StyledTableCell>
                <StyledTableCell align="center" sx={{ fontWeight: 'bold' }}>
                  {row.isPast ? (
                    <Chip 
                        label={`${row.homeScore} : ${row.awayScore}`} 
                        size="small" 
                        sx={{ 
                            fontFamily: 'comfortaa', 
                            fontWeight: 'bold', 
                            backgroundColor: theme.palette.grey[700], 
                            color: theme.palette.grey[100],
                            fontSize: isMobile ? '0.65rem' : '0.8rem',
                            height: isMobile ? '18px' : '22px', 
                            lineHeight: isMobile ? '18px' : '22px',
                        }}
                    />
                  ) : (
                    <Typography variant="caption" sx={{color: theme.palette.grey[500], fontSize: isMobile ? '0.7rem' : 'inherit'}}>vs.</Typography>
                  )}
                </StyledTableCell>
                {/* Die sx-Prop hier wurde vereinfacht, um die Standard-Zentrierung zu nutzen */}
                <StyledTableCell sx={{ pt: isMobile ? 0.5 : undefined /* Explizites pt nur für Mobile, wenn nötig */ }}>
                  <Box sx={{ 
                      display: 'flex', 
                      flexDirection: isMobile ? 'column' : 'row', 
                      alignItems: 'center', 
                      justifyContent: isMobile ? 'center' : 'flex-start',
                      textAlign: isMobile ? 'center' : 'left',
                    }}
                  >
                    <Avatar 
                        alt={`${row.awayTeam} Logo`} 
                        sx={{ 
                            width: isMobile ? 24 : 20, 
                            height: isMobile ? 24 : 20, 
                            mb: isMobile ? 0.5 : 0, 
                            mr: isMobile ? 0 : 1, 
                            fontSize: isMobile ? '0.8rem' : '0.7rem',
                            color: theme.palette.getContrastText(row.awayLogoColor || theme.palette.grey[700]), 
                            backgroundColor: row.awayLogoColor || theme.palette.grey[700] 
                        }} 
                    >
                        {row.awayTeam.substring(0,1).toUpperCase()}
                    </Avatar>
                    <Typography variant="body2" sx={{ fontFamily: 'comfortaa', color: theme.palette.grey[100], fontSize: isMobile ? '0.65rem' : '0.8rem', whiteSpace: isMobile? 'normal' : 'nowrap', overflow: 'hidden', textOverflow: 'ellipsis' }}>
                        {row.awayTeam}
                    </Typography>
                  </Box>
                </StyledTableCell>
                <StyledTableCell hideOnMobile={true}>{row.location}</StyledTableCell>
              </TableRow>
            ))}
          </TableBody>
        </Table>
      </TableContainer>
    </Container>
  );
};

export default FixtureList;
