import * as React from 'react';
import AppBar from '@mui/material/AppBar';
import Box from '@mui/material/Box';
import Toolbar from '@mui/material/Toolbar';
import IconButton from '@mui/material/IconButton';
import Typography from '@mui/material/Typography';
// import Menu from '@mui/material/Menu'; // Wird für das Navigationsmenü nicht mehr direkt so genutzt
import MenuIcon from '@mui/icons-material/Menu';
import Menu from '@mui/icons-material/Menu';
import Container from '@mui/material/Container';
import Avatar from '@mui/material/Avatar';
import Button from '@mui/material/Button';
import Tooltip from '@mui/material/Tooltip';
import MenuItem from '@mui/material/MenuItem'; // Kann im Drawer oder User-Menü weiterhin nützlich sein
import AdbIcon from '@mui/icons-material/Adb';

// NEU: Drawer und List-Komponenten für den Inhalt des Drawers
import Drawer from '@mui/material/Drawer';
import List from '@mui/material/List';
import ListItem from '@mui/material/ListItem';
import ListItemButton from '@mui/material/ListItemButton';
import ListItemText from '@mui/material/ListItemText';
// Divider kann nützlich sein, um Sektionen im Drawer zu trennen
// import Divider from '@mui/material/Divider';

const pages = ['Home', 'Ergebnisse', 'Historiker-Ecke', 'Kontakt', 'Teams'];
const settings = ['Profile', 'Account', 'Dashboard', 'Logout']; // Für das User-Menü (Avatar)

function Header() {
  // State für den User-Menü-Dropdown (bleibt gleich)
  const [anchorElUser, setAnchorElUser] = React.useState(null);

  // NEU: State für den Navigations-Drawer (mobil)
  const [mobileOpen, setMobileOpen] = React.useState(false);

  const handleDrawerToggle = () => {
    setMobileOpen((prevState) => !prevState);
  };

  const handleOpenUserMenu = (event) => {
    setAnchorElUser(event.currentTarget);
  };

  const handleCloseUserMenu = () => {
    setAnchorElUser(null);
  };

  // Inhalt des Drawers
  const drawerContent = (
    <Box
      sx={{ width: 250 }} // Breite des Drawers
      role="presentation"
      onClick={handleDrawerToggle} // Schließt den Drawer bei Klick auf einen Link (optional)
      onKeyDown={handleDrawerToggle} // Schließt den Drawer bei Tastendruck (optional)
    >
      <List>
        {/* Optional: Ein Titel oder Logo im Drawer */}
        <ListItem>
           <Typography variant="h6" sx={{ my: 2 }}>Menü</Typography>
        </ListItem>
        {/* <Divider /> */}
        {pages.map((page) => (
          <ListItem key={page} disablePadding>
            <ListItemButton component="a" href={`/${page.toLowerCase().replace(/\s+/g, '-')}`}> {/* Passe href an deine Routing-Logik an */}
              <ListItemText primary={page} />
            </ListItemButton>
          </ListItem>
        ))}
      </List>
    </Box>
  );

  return (
    <> {/* React.Fragment oder <> ist hier nötig, da Drawer außerhalb der AppBar gerendert wird */}
      <AppBar position="sticky" sx={{ backgroundColor: '#222' }}> {/* Deine dunkle AppBar */}
        <Container maxWidth="xl">
          <Toolbar disableGutters>
            {/* Logo und Desktop-Titel */}
            <AdbIcon sx={{ display: { xs: 'none', md: 'flex' }, mr: 1 }} />
            <Typography
              variant="h6"
              noWrap
              component="a"
              href="/" // Link zur Startseite
              sx={{
                mr: 2,
                display: { xs: 'none', md: 'flex' },
                fontFamily: 'monospace',
                fontWeight: 700,
                letterSpacing: '.3rem',
                color: 'white',
                textDecoration: 'none',
              }}
            >
              Bunte Liga Freiburg
            </Typography>

            {/* Mobile Menu Icon (öffnet den Drawer) */}
            <Box sx={{ flexGrow: 1, display: { xs: 'flex', md: 'none' } }}>
              <IconButton
                size="large"
                aria-label="open navigation menu"
                aria-controls="menu-appbar-drawer" // ID für Barrierefreiheit anpassen
                aria-haspopup="true"
                onClick={handleDrawerToggle} // Öffnet/Schließt den Drawer
                color="inherit"
              >
                <MenuIcon />
              </IconButton>
              {/* Die alte <Menu>-Komponente für die Navigation ist hier entfernt */}
            </Box>

            {/* Mobiler Titel (wenn der Drawer zu ist) */}
            <AdbIcon sx={{ display: { xs: 'flex', md: 'none' }, mr: 1 }} />
            <Typography
              variant="h5"
              noWrap
              component="a"
              href="/" // Link zur Startseite
              sx={{
                mr: 2,
                display: { xs: 'flex', md: 'none' },
                flexGrow: 1,
                fontFamily: 'monospace',
                fontWeight: 700,
                letterSpacing: '.3rem',
                color: 'white',
                textDecoration: 'none',
              }}
            >
              Bunte Liga Freiburg
            </Typography>

            {/* Desktop Navigation Links */}
            <Box sx={{ flexGrow: 1, display: { xs: 'none', md: 'flex' } }}>
              {pages.map((page) => (
                <Button
                  key={page}
                  // onClick={handleCloseNavMenu} // Nicht mehr nötig, wenn es kein Dropdown ist
                  component="a" // Um als Link zu fungieren
                  href={`/${page.toLowerCase().replace(/\s+/g, '-')}`} // Passe href an deine Routing-Logik an
                  sx={{ my: 2, color: 'white', display: 'block' }}
                >
                  {page}
                </Button>
              ))}
            </Box>

            {/* User Menu (Avatar und Dropdown, bleibt gleich) */}
            <Box sx={{ flexGrow: 0 }}>
              <Tooltip title="Open settings">
                <IconButton onClick={handleOpenUserMenu} sx={{ p: 0 }}>
                  <Avatar alt="Remy Sharp" src="/static/images/avatar/2.jpg" />
                </IconButton>
              </Tooltip>
              <Menu // Dies ist das User-Menü, nicht das Navigationsmenü
                sx={{ mt: '45px' }}
                id="menu-appbar-user" // Eigene ID
                anchorEl={anchorElUser}
                anchorOrigin={{
                  vertical: 'top',
                  horizontal: 'right',
                }}
                keepMounted
                transformOrigin={{
                  vertical: 'top',
                  horizontal: 'right',
                }}
                open={Boolean(anchorElUser)}
                onClose={handleCloseUserMenu}
              >
                {settings.map((setting) => (
                  <MenuItem key={setting} onClick={handleCloseUserMenu}>
                    <Typography textAlign="center">{setting}</Typography>
                  </MenuItem>
                ))}
              </Menu>
            </Box>
          </Toolbar>
        </Container>
      </AppBar>

      {/* Der Navigations-Drawer für mobile Ansichten */}
      <Drawer
        anchor="left" // Hier kannst du 'left' oder 'right' wählen
        open={mobileOpen}
        onClose={handleDrawerToggle}
        ModalProps={{
          keepMounted: true, // Bessere Performance auf Mobilgeräten beim Schließen.
        }}
        sx={{
          display: { xs: 'block', md: 'none' }, // Nur auf mobilen Geräten anzeigen
          '& .MuiDrawer-paper': { boxSizing: 'border-box', width: 250 }, // Styling für das Drawer-Papier
        }}
        id="menu-appbar-drawer" // ID für Barrierefreiheit
      >
        {drawerContent}
      </Drawer>
    </>
  );
}
export default Header;
