import * as React from 'react';
import AppBar from '@mui/material/AppBar';
import Box from '@mui/material/Box';
import Toolbar from '@mui/material/Toolbar';
import IconButton from '@mui/material/IconButton';
import Typography from '@mui/material/Typography';
import MenuIcon from '@mui/icons-material/Menu';
import Container from '@mui/material/Container';
// import Avatar from '@mui/material/Avatar'; 
import Button from '@mui/material/Button';
// import Tooltip from '@mui/material/Tooltip';
import AdbIcon from '@mui/icons-material/Adb';
import Drawer from '@mui/material/Drawer';
import ChevronRightIcon from '@mui/icons-material/ChevronRight';
import ChevronLeftIcon from '@mui/icons-material/ChevronLeft';
import ListItem from '@mui/material/ListItem';
import ListItemButton from '@mui/material/ListItemButton';
import ListItemIcon from '@mui/material/ListItemIcon';
import ListItemText from '@mui/material/ListItemText';
import Divider from '@mui/material/Divider';
import List from '@mui/material/List';
import { styled, useTheme } from '@mui/material/styles';

import HomeIcon from '@mui/icons-material/Home';
import TableViewIcon from '@mui/icons-material/TableView';
import Diversity3Icon from '@mui/icons-material/Diversity3';
import ScheduleIcon from '@mui/icons-material/Schedule';
import PermContactCalendarIcon from '@mui/icons-material/PermContactCalendar';
import ListIcon from '@mui/icons-material/List';
import { useNavigate } from 'react-router-dom';
import logo from '../img/logo.png';



const pages = [
  { text: 'Home', iconName: 'HomeIcon', path: '/' },
  { text: 'Ergebnisse', iconName: 'TableViewIcon', path: '/ergebnisse' },
  { text: 'Teams', iconName: 'Diversity3Icon', path: '/teams' },
  { text: 'Historiker-Ecke', iconName: 'ScheduleIcon', path: '/historie' },
  { text: 'Kontakt', iconName: 'PermContactCalendarIcon', path: '/kontakt' },
];
// const settings = ['Profile', 'Account', 'Dashboard', 'Logout'];

const iconMap = {
  HomeIcon: HomeIcon,
  TableViewIcon: TableViewIcon,
  Diversity3Icon: Diversity3Icon,
  ScheduleIcon: ScheduleIcon,
  PermContactCalendarIcon: PermContactCalendarIcon,
  DefaultIcon: ListIcon,
};

const drawerWidth = 240;
const logoHeight = 35; // Feste Höhe für das Logo
const logoWidth = 45; // Feste Breite für das Logo 

const CustomDrawerHeader = styled('div')(({ theme }) => ({
  display: 'flex',
  alignItems: 'center',
  padding: theme.spacing(0, 1),
  ...theme.mixins.toolbar,
  justifyContent: 'flex-start',
}));

function Header() {
  let navigate = useNavigate();
  const theme = useTheme();
  const [mobileOpen, setMobileOpen] = React.useState(false); // State für den Drawer
  // const [anchorElUser, setAnchorElUser] = React.useState(null); // Für User-Menü, derzeit auskommentiert

  const handleDrawerToggle = () => {
    setMobileOpen(!mobileOpen);
  };

  // const handleOpenUserMenu = (event) => {
  //   setAnchorElUser(event.currentTarget);
  // };

  // const handleCloseUserMenu = () => {
  //   // setAnchorElUser(null);
  // };

  const drawerContent = (
    <Box
      sx={{
        width: drawerWidth,
        backgroundColor: theme.palette.grey[800],
        color: theme.palette.common.white,
        height: '100%',
      }}
      onClick={handleDrawerToggle}
      onKeyDown={handleDrawerToggle}
    >
      <CustomDrawerHeader sx={{ justifyContent: 'flex-end' }}>
        <IconButton onClick={handleDrawerToggle} sx={{ color: theme.palette.common.white }}>
          {theme.direction === 'rtl' ? <ChevronLeftIcon /> : <ChevronRightIcon />}
        </IconButton>
      </CustomDrawerHeader>
      <Divider sx={{ borderColor: theme.palette.grey[700] }} />
      <List>
        {pages.map((pageItem) => {
          const IconComponent = iconMap[pageItem.iconName] || iconMap.DefaultIcon;
          return (
            <ListItem key={pageItem.text} disablePadding>
              <ListItemButton

                onClick={() => { navigate(pageItem.path); handleDrawerToggle(); }}
              >
                <ListItemIcon sx={{ color: theme.palette.common.white }}>
                  {IconComponent ? <IconComponent /> : null}
                </ListItemIcon>
                <ListItemText primary={pageItem.text} />
              </ListItemButton>
            </ListItem>
          );
        })}
      </List>
    </Box>
  );

  return (
    <AppBar position="sticky" sx={{ backgroundColor: '#111' }}>
      <Container maxWidth="xl">
        <Toolbar disableGutters>

          <Box sx={{ display: { xs: 'none', md: 'flex' }, mr: 1, alignItems: 'center' }}>
            <img
              src={logo} 
              alt="Bunte Liga Freiburg Logo"
              style={{ height: `${logoHeight}px`, width: `${logoWidth}px`, objectFit: 'contain' }}
              onError={(e) => { e.currentTarget.src = `https://placehold.co/${logoWidth}x${logoHeight}/eeeeee/222222?text=Logo+Error`; e.currentTarget.alt = 'Fehler beim Laden des Logos'; }}
            />
          </Box>
          <Typography
            variant="h6"
            noWrap
            component="a"
            href="/"
            sx={{
              mr: 2,
              ms: 2,
              display: { xs: 'none', md: 'flex' },
              fontFamily: 'monospace',
              fontWeight: 700,
              letterSpacing: '.3rem',
              color: 'inherit',
              textDecoration: 'none',
              alignItems: 'center'
            }}
          >
            DIE BUNTE LIGA
          </Typography>
          
          {/* Mobile Header Left Part (Menu Icon) */}
          <Box sx={{ display: { xs: 'flex', md: 'none' }, alignItems: 'center' }}>
            <IconButton
              size="large"
              aria-label="open navigation menu"
              aria-controls="menu-appbar-mobile"
              aria-haspopup="true"
              onClick={handleDrawerToggle}
              color="inherit"
            >
              <MenuIcon />
            </IconButton>
          </Box>
          
          {/* Mobile Header Middle Part (Logo and Title) */}
          {/* This Box groups Logo and Title for mobile and allows them to take available space */}
          <Box sx={{ flexGrow: 1, display: { xs: 'flex', md: 'none' }, alignItems: 'center', justifyContent: 'flex-start' }}>
            <Box sx={{ display: 'flex', alignItems: 'center', mx: 1 }} component="a" href="/" color="inherit" underline="none">
                <img
                  src={logo} 
                  alt="Bunte Liga Freiburg Logo Mobile"
                  style={{ height: `${logoHeight}px`, width: `${logoWidth}px`, objectFit: 'contain' }}
                />
            </Box>
            <Typography
              variant="h6" 
              noWrap
              component="a"
              href="/"
              sx={{
                display: 'flex', 
                fontFamily: 'monospace',
                fontWeight: 800,
                letterSpacing: '0.1rem', // Slightly reduced for mobile
                color: 'inherit',
                textDecoration: 'none',
                alignItems: 'center'
              }}
            >
              DIE BUNTE LIGA
            </Typography>
          </Box>
          
          {/* Drawer (remains the same, its positioning is modal) */}
          <Drawer
            variant="temporary"
            anchor="right"
            open={mobileOpen}
            onClose={handleDrawerToggle}
            ModalProps={{
              keepMounted: true,
            }}
            sx={{
              display: { xs: 'block', md: 'none' },
              '& .MuiDrawer-paper': {
                boxSizing: 'border-box',
                width: drawerWidth,
              },
            }}
          >
            {drawerContent}
          </Drawer>

          {/* Desktop Navigation Buttons */}
          <Box sx={{ flexGrow: 1, display: { xs: 'none', md: 'flex' }, justifyContent: 'flex-end' }}>
            {pages.map((pageItem) => (
              <Button
                key={pageItem.text}
                sx={{ my: 2, color: 'white', display: 'block', '&:hover': { backgroundColor: 'rgba(255,255,255,0.1)' } }}
                className="rounded-md"
                onClick={() => { navigate(pageItem.path);}} // handleDrawerToggle removed for desktop
              >
                {pageItem.text}
              </Button>
            ))}
          </Box>
        </Toolbar>
      </Container>
    </AppBar>
  );
}
export default Header;
