import * as React from 'react';
import AppBar from '@mui/material/AppBar';
import Box from '@mui/material/Box';
import Toolbar from '@mui/material/Toolbar';
import IconButton from '@mui/material/IconButton';
import Typography from '@mui/material/Typography';
// import Menu from '@mui/material/Menu'; // Nicht mehr direkt für Nav-Menü verwendet
import MenuIcon from '@mui/icons-material/Menu';
import Container from '@mui/material/Container';
// import Avatar from '@mui/material/Avatar'; // Auskommentiert wie im User-Code
import Button from '@mui/material/Button';
// import Tooltip from '@mui/material/Tooltip'; // Auskommentiert wie im User-Code
// import MenuItem from '@mui/material/MenuItem'; // Nicht mehr direkt für Nav-Menü verwendet
import AdbIcon from '@mui/icons-material/Adb';
import Drawer from '@mui/material/Drawer';
import ChevronRightIcon from '@mui/icons-material/ChevronRight'; // Für Drawer-Header
import ChevronLeftIcon from '@mui/icons-material/ChevronLeft';   // Für Drawer-Header (RTL)
import ListItem from '@mui/material/ListItem';
import ListItemButton from '@mui/material/ListItemButton';
import ListItemIcon from '@mui/material/ListItemIcon';
import ListItemText from '@mui/material/ListItemText';
import Divider from '@mui/material/Divider';
import List from '@mui/material/List';
import { styled, useTheme } from '@mui/material/styles';

// Icon Imports
import HomeIcon from '@mui/icons-material/Home';
import TableViewIcon from '@mui/icons-material/TableView';
import Diversity3Icon from '@mui/icons-material/Diversity3';
import ScheduleIcon from '@mui/icons-material/Schedule';
import PermContactCalendarIcon from '@mui/icons-material/PermContactCalendar';
import ListIcon from '@mui/icons-material/List'; // Fallback-Icon

// Daten für die Navigationselemente
// Struktur: [Anzeigetext, IconNameString, PfadFürRouter (optional)]
const pages = [
  { text: 'Home', iconName: 'HomeIcon', path: '/' },
  { text: 'Ergebnisse', iconName: 'TableViewIcon', path: '/ergebnisse' },
  { text: 'Teams', iconName: 'Diversity3Icon', path: '/teams' },
  { text: 'Historiker-Ecke', iconName: 'ScheduleIcon', path: '/historie' },
  { text: 'Kontakt', iconName: 'PermContactCalendarIcon', path: '/kontakt' },
];
// const settings = ['Profile', 'Account', 'Dashboard', 'Logout']; // Auskommentiert

// Mapping von Icon-Namen (Strings) zu den importierten Icon-Komponenten
const iconMap = {
  HomeIcon: HomeIcon,
  TableViewIcon: TableViewIcon,
  Diversity3Icon: Diversity3Icon,
  ScheduleIcon: ScheduleIcon,
  PermContactCalendarIcon: PermContactCalendarIcon,
  DefaultIcon: ListIcon, // Fallback-Icon
};

const drawerWidth = 240; // Breite des Drawers

const CustomDrawerHeader = styled('div')(({ theme }) => ({
  display: 'flex',
  alignItems: 'center',
  padding: theme.spacing(0, 1),
  ...theme.mixins.toolbar,
  justifyContent: 'flex-start', // Icon links im Drawer Header, wenn Drawer von rechts kommt
}));

function Header() {
  const theme = useTheme();
  const [mobileOpen, setMobileOpen] = React.useState(false); // State für den Drawer
  // const [anchorElUser, setAnchorElUser] = React.useState(null); // Für User-Menü, derzeit auskommentiert

  const handleDrawerToggle = () => {
    setMobileOpen(!mobileOpen);
  };

  // const handleOpenUserMenu = (event) => {
  //   setAnchorElUser(event.currentTarget);
  // };

  const handleCloseUserMenu = () => {
    // setAnchorElUser(null);
  };

  const drawerContent = (
    <Box
      sx={{ 
        width: drawerWidth, 
        // Styling für den Inhalt des Drawers, wenn er dunkel sein soll
        backgroundColor: theme.palette.grey[800], 
        color: theme.palette.common.white,
        height: '100%', // Damit der Hintergrund die volle Höhe einnimmt
      }}
      onClick={handleDrawerToggle} // Schließt den Drawer bei Klick auf den Inhalt (optional)
      onKeyDown={handleDrawerToggle} // Für Barrierefreiheit
    >
      <CustomDrawerHeader sx={{ justifyContent: 'flex-end' /* Icon zum Schließen rechts */ }}>
        <IconButton onClick={handleDrawerToggle} sx={{ color: theme.palette.common.white }}>
          {theme.direction === 'rtl' ? <ChevronLeftIcon /> : <ChevronRightIcon />}
        </IconButton>
      </CustomDrawerHeader>
      <Divider sx={{ borderColor: theme.palette.grey[700] }} />
      <List>
        {pages.map((pageItem) => {
          const IconComponent = iconMap[pageItem.iconName] || iconMap.DefaultIcon;
          return (
            <ListItem key={pageItem.text} disablePadding>
              <ListItemButton 
                // Hier könntest du react-router-dom <Link component={Link} to={pageItem.path}> verwenden
                // oder eine Navigationsfunktion aufrufen:
                // onClick={() => { navigate(pageItem.path); handleDrawerToggle(); }}
              >
                <ListItemIcon sx={{ color: theme.palette.common.white }}>
                  {IconComponent ? <IconComponent /> : null}
                </ListItemIcon>
                <ListItemText primary={pageItem.text} />
              </ListItemButton>
            </ListItem>
          );
        })}
      </List>
    </Box>
  );

  return (
    <AppBar position="sticky" sx={{ backgroundColor: '#111' }}>
      <Container maxWidth="xl">
        <Toolbar disableGutters>
          {/* Desktop Logo */}
          <AdbIcon sx={{ display: { xs: 'none', md: 'flex' }, mr: 1 }} />
          <Typography
            variant="h6"
            noWrap
            component="a"
            href="/" // Link zur Startseite
            sx={{
              mr: 2,
              display: { xs: 'none', md: 'flex' },
              fontFamily: 'monospace',
              fontWeight: 700,
              letterSpacing: '.3rem',
              color: 'inherit',
              textDecoration: 'none',
            }}
          >
            Bunte Liga Freiburg
          </Typography>

          {/* Mobile Menu Icon & Drawer */}
          <Box sx={{ flexGrow: 1, display: { xs: 'flex', md: 'none' } }}>
            <IconButton
              size="large"
              aria-label="open navigation menu"
              aria-controls="menu-appbar-mobile"
              aria-haspopup="true"
              onClick={handleDrawerToggle}
              color="inherit"
            >
              <MenuIcon />
            </IconButton>
            <Drawer
              variant="temporary" // "temporary" für Overlay, "persistent" oder "permanent" für andere Effekte
              anchor="right"
              open={mobileOpen}
              onClose={handleDrawerToggle}
              ModalProps={{
                keepMounted: true, // Bessere Performance auf Mobilgeräten.
              }}
              sx={{
                display: { xs: 'block', md: 'none' },
                '& .MuiDrawer-paper': { 
                  boxSizing: 'border-box', 
                  width: drawerWidth,
                  // Direkte Style-Anwendung für das Paper-Element nicht mehr hier,
                  // da der Hintergrund im drawerContent <Box> gesetzt wird.
                  // Du könntest hier aber noch Border, etc. setzen falls nötig.
                },
              }}
            >
              {drawerContent}
            </Drawer>
          </Box>

          {/* Mobile Logo */}
          <AdbIcon sx={{ display: { xs: 'flex', md: 'none' }, mr: 1 }} />
          <Typography
            variant="h6" // Angepasst zu h6 für Konsistenz
            noWrap
            component="a"
            href="/" // Link zur Startseite
            sx={{
              mr: 1,
              display: { xs: 'flex', md: 'none' },
              flexGrow: 1,
              fontFamily: 'monospace', // Angepasst für Konsistenz
              fontWeight: 700,
              letterSpacing: '.1rem', // '.2rem' war dein Wert
              color: 'inherit',
              textDecoration: 'none',
            }}
          >
            Bunte Liga Freiburg
          </Typography>

          {/* Desktop Navigation Links */}
          <Box sx={{ flexGrow: 1, display: { xs: 'none', md: 'flex' }, justifyContent: 'flex-end' }}>
            {pages.map((pageItem) => (
              <Button
                key={pageItem.text}
                // onClick={() => navigate(pageItem.path)} // Beispiel für Navigation
                sx={{ my: 2, color: 'white', display: 'block', '&:hover': { backgroundColor: 'rgba(255,255,255,0.1)'} }}
                className="rounded-md"
              >
                {pageItem.text}
              </Button>
            ))}
          </Box>

          {/* User Menu (auskommentiert wie in deinem vorherigen Code) */}
          {/* <Box sx={{ flexGrow: 0 }}>
            <Tooltip title="Open settings">
              <IconButton onClick={handleOpenUserMenu} sx={{ p: 0 }}>
                <Avatar alt="Remy Sharp" src="/static/images/avatar/2.jpg" />
              </IconButton>
            </Tooltip>
            <Menu
              sx={{ mt: '45px' }}
              id="menu-appbar-user"
              anchorEl={anchorElUser}
              anchorOrigin={{
                vertical: 'top',
                horizontal: 'right',
              }}
              keepMounted
              transformOrigin={{
                vertical: 'top',
                horizontal: 'right',
              }}
              open={Boolean(anchorElUser)}
              onClose={handleCloseUserMenu}
            >
              {settings.map((setting) => (
                <MenuItem key={setting} onClick={handleCloseUserMenu}>
                  <Typography textAlign="center">{setting}</Typography>
                </MenuItem>
              ))}
            </Menu>
          </Box> */}
        </Toolbar>
      </Container>
    </AppBar>
  );
}
export default Header;
