import * as React from 'react';
import { styled, useTheme } from '@mui/material/styles';
import { Card, CardHeader, CardContent, CardActions, Collapse, IconButton, Typography, Box } from '@mui/material'; // CardActions importiert
import ExpandMoreIcon from '@mui/icons-material/ExpandMore';

// Ein gestylter IconButton für die "Aufklappen"-Animation, wie in den MUI-Beispielen
const ExpandMore = styled((props) => {
  const { expand, ...other } = props;
  return <IconButton {...other} />;
})(({ theme, expand }) => ({
  transform: !expand ? 'rotate(0deg)' : 'rotate(180deg)',
  transition: theme.transitions.create('transform', {
    duration: theme.transitions.duration.shortest,
  }),
}));

const NewsCard = ({ title, subtitle, date, content }) => {
  const theme = useTheme();
  const [expanded, setExpanded] = React.useState(false);

  // Setzt den "expanded"-Status zurück, wenn sich der Titel ändert (also eine neue News geladen wird)
  React.useEffect(() => {
    setExpanded(false);
  }, [title]);

  const handleExpandClick = () => {
    setExpanded(!expanded);
  };

  return (
    <Card 
      sx={{
        width: '100%', // Nimmt die volle Breite des Parent-Containers
        backgroundColor: '#111',
        color: theme.palette.grey[300],
        border: `1px solid ${theme.palette.grey[800]}`,
        borderRadius: theme.shape.borderRadius,
        display: 'flex', // Macht die Karte zu einem Flex-Container
        flexDirection: 'column', // Ordnet die Kinder vertikal an
        minHeight: '220px', // Mindesthöhe für die Karte, damit sie nicht zu klein wird
      }}
    >
      <CardHeader
        title={
          <Typography
            variant="h6" // Basis-Variante, Schriftgröße wird per sx angepasst
            noWrap // Verhindert Zeilenumbruch
            sx={{
              fontFamily: 'comfortaa', 
              fontWeight: 700,
              color: '#fff',
              fontSize: { 
                  xs: '1.1rem', 
                  sm: '1.25rem', 
              },
              overflow: 'hidden', 
              textOverflow: 'ellipsis',
            }}
          >
            {title}
          </Typography>
        }
        subheader={
          <Typography
            variant="body2" // Basis-Variante
            noWrap // Verhindert Zeilenumbruch
            sx={{
              fontFamily: 'comfortaa', 
              color: theme.palette.grey[500],
              fontSize: {
                  xs: '0.75rem',
                  sm: '0.875rem'
              },
              overflow: 'hidden',
              textOverflow: 'ellipsis',
            }}
          >
            {date}
          </Typography>
        }
        // Action (Expand-Button) wird nicht mehr hier, sondern in CardActions platziert
        sx={{
            pb: 1, 
            // Wichtig, damit der Header-Inhalt nicht den (nicht mehr vorhandenen) Action-Button überlappt
            // Dies ist immer noch nützlich, um sicherzustellen, dass der Textbereich nicht überläuft.
            '& .MuiCardHeader-content': {
                overflow: 'hidden', 
            }
        }}
      />
      <CardContent sx={{ 
        pt: 0, 
        flexGrow: 1, // Erlaubt diesem Bereich, den verfügbaren Platz auszufüllen
        display: 'flex', 
        flexDirection: 'column', 
      }}>
        <Typography variant="body1" sx={{ 
            fontFamily: 'comfortaa',
            // Optionales Text-Handling für lange Untertitel, falls benötigt
            // overflow: 'hidden',
            // textOverflow: 'ellipsis',
            // display: '-webkit-box',
            // WebkitLineClamp: 3, 
            // WebkitBoxOrient: 'vertical',
        }}>
          {subtitle}
        </Typography>
      </CardContent>
      <CardActions sx={{ pt:0, pb:1, px:2, mt: 'auto' }}>
        <ExpandMore
            expand={expanded}
            onClick={handleExpandClick}
            aria-expanded={expanded}
            aria-label="mehr anzeigen"
            sx={{ 
                color: theme.palette.grey[400],
                marginLeft: 'auto', // Schiebt den Button nach rechts
            }}
          >
            <ExpandMoreIcon />
        </ExpandMore>
      </CardActions>
      <Collapse in={expanded} timeout="auto" unmountOnExit>
        <CardContent sx={{ 
            borderTop: `1px solid ${theme.palette.grey[800]}`,
        }}>
          <Typography paragraph sx={{ fontFamily: 'comfortaa', lineHeight: 1.7, color: theme.palette.grey[300], mb:0 }}>
            {content}
          </Typography>
        </CardContent>
      </Collapse>
    </Card>
  );
};

export default NewsCard;
