import React from 'react';
import { useLocation } from 'react-router-dom'; // useLocation importieren
import NewsCarousel from '../components/News/News';
import LeagueTable from '../components/LeagueTable';
import FixtureList from '../components/FixtureList';
// import './HomePage.css'; // Erstelle HomePage.css für spezifische Styles

const HomePage = () => {
  const location = useLocation(); // Aktuelle Location-Informationen abrufen

  React.useEffect(() => {
    if (location.hash) {
      // Den Hash-Wert ohne das '#' extrahieren, um die ID zu bekommen
      const id = location.hash.replace('#', '');
      const element = document.getElementById(id);
      if (element) {
        // Zum Element scrollen
        element.scrollIntoView({ behavior: 'smooth' }); // 'smooth' für sanftes Scrollen
      }
    }
  }, [location.hash]); // Effekt erneut ausführen, wenn sich der Hash ändert

  return (
    <div className="homepage">
      <NewsCarousel />
      <LeagueTable title="Liga-Tabelle" form={true}/> {/* Die 'form' Prop wurde entfernt, da sie in deiner LeagueTable nicht verwendet wird */}
      
      {/* Stelle sicher, dass diese section die ID hat, zu der du scrollen möchtest */}
      <section id="Ergebnisse"> 
        <FixtureList />
      </section>
      
      {/* Du könntest hier weitere Sektionen mit IDs hinzufügen */}
      {/* <section id="Tabelle">
        <LeagueTable title="Liga-Tabelle" />
      </section>
      <section id="News">
        <NewsCarousel />
      </section> 
      */}
    </div>
  );
};

export default HomePage;
