import * as React from 'react';
import { Box, Container, IconButton, Typography, useTheme } from '@mui/material'; // useTheme importiert
import ArrowBackIosNewIcon from '@mui/icons-material/ArrowBackIosNew';
import ArrowForwardIosIcon from '@mui/icons-material/ArrowForwardIos';
import NewsCard from './NewsCard'; // Import der NewsCard Komponente

// Beispieldaten für die News (können später von einer API kommen)
const newsData = [
  {
    date: '31. Mai 2025',
    title: 'Saisonstart der Bunten Liga Freiburg terminiert!',
    subtitle: 'Die neue Saison der Bunten Liga Freiburg beginnt am ersten Septemberwochenende. Alle Teams sind aufgerufen, ihre Kaderlisten bis Ende August einzureichen.',
    content: 'Nach einer langen und erholsamen Sommerpause freuen wir uns, den offiziellen Start der Saison 2025/2026 bekannt zu geben. Der erste Spieltag findet am Samstag, den 6. September statt. Die Spielpläne werden in den kommenden Wochen veröffentlicht. Wir bitten alle Team-Kapitäne, die Verfügbarkeit ihrer Spieler zu prüfen und die finalen Kader bis zum 31. August über das Online-Portal zu melden. Wir wünschen allen eine spannende und faire Saison!',
  },
  {
    date: '28. Mai 2025',
    title: 'Neues Regelwerk für Fair-Play und mehr Spaß auf dem Platz',
    subtitle: 'Um den fairen Wettbewerb weiter zu fördern und den Spaßfaktor zu erhöhen, wurden einige Anpassungen im Regelwerk vorgenommen, die ab sofort gelten.',
    content: 'Das Liga-Komitee hat in Zusammenarbeit mit den Team-Vertretern ein überarbeitetes Fair-Play-Regelwerk verabschiedet. Die wichtigste Änderung betrifft die Handhabung von Zeitstrafen, die nun konsequenter geahndet werden. Zudem wird die Fair-Play-Wertung am Ende der Saison ein höheres Gewicht haben. Neu ist auch eine "Tor des Monats"-Wahl, um besonders sehenswerte Treffer zu würdigen. Alle Details zu den neuen Regeln findet ihr im Download-Bereich auf der Webseite. Fairness und Respekt auf und neben dem Platz sind die Grundpfeiler unserer Liga.',
  },
  {
    date: '25. Mai 2025',
    title: 'Großes Team-Treffen und Saisonvorbereitung im Juli geplant',
    subtitle: 'Einladung zum jährlichen Treffen aller Mannschafts-Kapitäne und Spieler am 15. Juli im Jos-Fritz-Café zur Saisonbesprechung und gemütlichem Beisammensein.',
    content: 'Wie jedes Jahr möchten wir alle Kapitäne, Co-Kapitäne und interessierte Spieler zu unserem jährlichen Treffen einladen. Dieses Jahr findet es am 15. Juli um 19:00 Uhr im Jos-Fritz-Café statt. Auf der Agenda stehen unter anderem die Vorstellung neuer Teams, die Diskussion über das neue Regelwerk, die Planung von gemeinsamen Events und die Vorbereitung auf die kommende Spielzeit. Für Getränke und kleine Snacks ist gesorgt. Wir freuen uns auf einen konstruktiven Austausch und zahlreiches Erscheinen.',
  },
];

const NewsCarousel = () => {
  const theme = useTheme(); // Theme für Zugriff auf Breakpoints etc.
  const [activeIndex, setActiveIndex] = React.useState(0);

  const handlePrevious = () => {
    setActiveIndex((prevIndex) => (prevIndex - 1 + newsData.length) % newsData.length);
  };

  const handleNext = () => {
    setActiveIndex((prevIndex) => (prevIndex + 1) % newsData.length);
  };

  const currentNews = newsData[activeIndex];

  return (
    <Container maxWidth="xl" sx={{ my: 4, px: { xs: 1, sm: 2 } /* Weniger Padding auf xs Screens */ }}>
      {/* Box für die Navigations-Buttons, jetzt über der Karte */}
      <Box 
        sx={{ 
          display: 'flex', 
          justifyContent: 'space-between', // Buttons an den Enden
          alignItems: 'center',
          mb: 2, // Abstand zur Karte
          width: '100%', 
          maxWidth: '900px', // Gleiche maximale Breite wie die Karte für visuelle Konsistenz
          mx: 'auto', // Zentriert die Button-Reihe, wenn sie schmaler als der Container ist
        }}
      >
        <IconButton 
            onClick={handlePrevious} 
            aria-label="vorherige nachricht"
            sx={{ 
                color: 'grey.400', 
                backgroundColor: 'rgba(255,255,255,0.05)',
                '&:hover': { backgroundColor: 'rgba(255,255,255,0.1)' } 
            }}
        >
          <ArrowBackIosNewIcon />
        </IconButton>
        <IconButton 
            onClick={handleNext} 
            aria-label="nächste nachricht"
            sx={{ 
                color: 'grey.400', 
                backgroundColor: 'rgba(255,255,255,0.05)',
                '&:hover': { backgroundColor: 'rgba(255,255,255,0.1)' } 
            }}
        >
          <ArrowForwardIosIcon />
        </IconButton>
      </Box>

      {/* Box für die News-Karte */}
      <Box sx={{ 
          width: '100%', 
          maxWidth: '900px', 
          mx: 'auto' /* Zentriert den Kartenblock */ 
        }}
      >
        {/* Der Key hier ist wichtig, damit React die NewsCard-Komponente bei Index-Änderung
            als neue Instanz behandelt und der useEffect für `setExpanded(false)` zuverlässig triggert.
        */}
        <NewsCard
          key={activeIndex} 
          title={currentNews.title}
          subtitle={currentNews.subtitle}
          date={currentNews.date}
          content={currentNews.content}
        />
      </Box>
    </Container>
  );
};

export default NewsCarousel;
