import React from 'react';
import NavigationCard from '../components/NavigationCard';
import NewsCarousel from '../components/news';
// import './HomePage.css'; // Erstelle HomePage.css für spezifische Styles

const HomePage = () => {
  const navItems = [
    {
      title: "Ergebnisse und Tabellen",
      subtitle: "Immer dran denken: Dabei sein ist alles!",
      link: "/ergebnisse" // Entspricht den Pfaden in App.js
    },
    {
      title: "Platz reservieren",
      subtitle: "Aktueller Spielplan & Platzvergabe",
      link: "/platzreservierung"
    },
    {
      title: "Historiker-Ecke",
      subtitle: "Die ewige Tabelle und alles Weitere für Statistik-Nerds",
      link: "/historie"
    },
    {
      title: "Kontakte",
      subtitle: "Für Fanpost, Fragen, Fakenews",
      link: "/kontakt"
    },
    {
      title: "Teams",
      subtitle: "Ja, es sind viele!",
      link: "/teams"
    }
  ];

const newsData = [
  {
    date: '31. Mai 2025',
    title: 'Saisonstart terminiert!',
    subtitle: 'Die neue Saison der Bunten Liga Freiburg beginnt am ersten Septemberwochenende. Alle Teams sind aufgerufen, ihre Kaderlisten bis Ende August einzureichen.',
    content: 'Nach einer langen und erholsamen Sommerpause freuen wir uns, den offiziellen Start der Saison 2025/2026 bekannt zu geben. Der erste Spieltag findet am Samstag, den 6. September statt. Die Spielpläne werden in den kommenden Wochen veröffentlicht. Wir bitten alle Team-Kapitäne, die Verfügbarkeit ihrer Spieler zu prüfen und die finalen Kader bis zum 31. August über das Online-Portal zu melden. Wir wünschen allen eine spannende und faire Saison!',
  },
  {
    date: '28. Mai 2025',
    title: 'Neues Regelwerk für Fair-Play',
    subtitle: 'Um den fairen Wettbewerb weiter zu fördern, wurden einige Anpassungen im Regelwerk vorgenommen, die ab sofort gelten.',
    content: 'Das Liga-Komitee hat in Zusammenarbeit mit den Team-Vertretern ein überarbeitetes Fair-Play-Regelwerk verabschiedet. Die wichtigste Änderung betrifft die Handhabung von Zeitstrafen, die nun konsequenter geahndet werden. Zudem wird die Fair-Play-Wertung am Ende der Saison ein höheres Gewicht haben. Alle Details zu den neuen Regeln findet ihr im Download-Bereich auf der Webseite. Fairness und Respekt auf und neben dem Platz sind die Grundpfeiler unserer Liga.',
  },
  {
    date: '25. Mai 2025',
    title: 'Team-Treffen im Juli',
    subtitle: 'Einladung zum jährlichen Treffen aller Mannschafts-Kapitäne am 15. Juli im Jos-Fritz-Café.',
    content: 'Wie jedes Jahr möchten wir alle Kapitäne und Co-Kapitäne zu unserem jährlichen Treffen einladen. Dieses Jahr findet es am 15. Juli um 19:00 Uhr im Jos-Fritz-Café statt. Auf der Agenda stehen unter anderem die Vorstellung neuer Teams, die Diskussion über das neue Regelwerk und die Planung von gemeinsamen Events. Für Getränke ist gesorgt. Wir freuen uns auf einen konstruktiven Austausch und zahlreiches Erscheinen.',
  },
];


  return (
    <div className="homepage">
      <NewsCarousel />
      <section className="hero-section">
        <h2>NEWS</h2>
        <p>Wir gratulieren dem 1. FC Ferdi Weiß zum Meistertitel der Saison 2024/25!!!</p>
        <h1>Hans ist der beste !!!</h1>
      </section>

      <section className="navigation-cards-container">
        {navItems.map((item, index) => (
          <NavigationCard
            key={index}
            title={item.title}
            subtitle={item.subtitle}
            link={item.link}
          />
        ))}
      </section>
    </div>
  );
};

export default HomePage;
