import * as React from 'react';
import {
  Box,
  Container,
  Typography,
  TextField,
  Button,
  Grid,
  Paper,
  MenuItem,
  Select,
  FormControl,
  InputLabel,
  useTheme,
  Divider,
  List,
  ListItem,
  ListItemText,
  ListItemIcon,
} from '@mui/material';
import SendIcon from '@mui/icons-material/Send';
import BusinessIcon from '@mui/icons-material/Business'; // Beispiel-Icon für Vorstand
import AccountBalanceWalletIcon from '@mui/icons-material/AccountBalanceWallet'; // Beispiel-Icon für Kassenwart
import SportsSoccerIcon from '@mui/icons-material/SportsSoccer'; // Beispiel-Icon für Spielausschuss
import GavelIcon from '@mui/icons-material/Gavel'; // Beispiel-Icon für Sportgericht
import SecurityIcon from '@mui/icons-material/Security'; // Beispiel-Icon für Prävention

// Farben basierend auf deiner vorherigen Auswahl
const blaugrün = '#00A99D';
const gelb = '#FFBF00';

const clubStructure = [
  { role: 'Vorstand', members: ['Max Mustermann', 'Erika Mustermann'], icon: <BusinessIcon sx={{ color: gelb }} /> },
  { role: 'Kassenwart', members: ['Peter Sparfuchs'], icon: <AccountBalanceWalletIcon sx={{ color: gelb }} /> },
  { role: 'Spielausschuss', members: ['Sabine Spielmacher', 'Kevin Kicker'], icon: <SportsSoccerIcon sx={{ color: gelb }} /> },
  { role: 'Sportgericht', members: ['Richter Gnadenlos', 'Justitia Fairplay'], icon: <GavelIcon sx={{ color: gelb }} /> },
  { role: 'Präventionsteam', members: ['Paula Passauf', 'Anton Aufmerksam'], icon: <SecurityIcon sx={{ color: gelb }} /> },
];

const ContactPage = () => {
  const theme = useTheme();
  const [formData, setFormData] = React.useState({
    recipient: '',
    name: '',
    email: '',
    subject: '',
    message: '',
  });

  const handleChange = (event) => {
    const { name, value } = event.target;
    setFormData((prevData) => ({
      ...prevData,
      [name]: value,
    }));
  };

  const handleSubmit = (event) => {
    event.preventDefault();
    // Hier würde die Logik zum Senden des Formulars stehen
    console.log('Formulardaten:', formData);
    // Optional: Formular nach dem Senden zurücksetzen
    setFormData({
      recipient: '',
      name: '',
      email: '',
      subject: '',
      message: '',
    });
    // Optional: Erfolgsmeldung anzeigen
    alert('Nachricht gesendet (simuliert)!');
  };

  const inputLabelStyles = {
    color: theme.palette.grey[400],
    fontFamily: 'comfortaa',
    '&.Mui-focused': {
      color: gelb, // Akzentfarbe für fokussiertes Label
    },
  };

  const textFieldStyles = {
    '& .MuiInputBase-input': {
      color: theme.palette.grey[200],
      fontFamily: 'comfortaa',
    },
    '& .MuiOutlinedInput-root': {
      '& fieldset': {
        borderColor: theme.palette.grey[700],
      },
      '&:hover fieldset': {
        borderColor: theme.palette.grey[500],
      },
      '&.Mui-focused fieldset': {
        borderColor: gelb, // Akzentfarbe für fokussierten Rahmen
      },
    },
    '& .MuiInputLabel-root': inputLabelStyles,
  };

  return (
    <Container maxWidth="lg" sx={{ py: 4, color: theme.palette.grey[300] }}>
      <Typography
        variant="h3"
        component="h1"
        gutterBottom
        textAlign="center"
        sx={{
          fontFamily: 'comfortaa',
          fontWeight: 700,
          color: blaugrün, // Hauptüberschrift in Blaugrün
          mb: 5,
        }}
      >
        KONTAKT
      </Typography>

      <Grid container spacing={5}>
        {/* Vereinsstruktur Abschnitt */}
        <Grid item xs={12} md={5}>
          <Typography
            variant="h5"
            component="h2"
            gutterBottom
            sx={{
              fontFamily: 'comfortaa',
              fontWeight: 600,
              color: theme.palette.grey[100],
              mb: 2,
              borderBottom: `2px solid ${gelb}`,
              pb: 1,
              display: 'inline-block',
            }}
          >
            Vereinsstruktur
          </Typography>
          <Paper
            elevation={3}
            sx={{
              p: 2.5,
              backgroundColor: 'rgba(30, 30, 30, 0.7)', // Leicht transparenter dunkler Hintergrund
              backdropFilter: 'blur(5px)',
              borderRadius: theme.shape.borderRadius,
              border: `1px solid ${theme.palette.grey[800]}`,
            }}
          >
            <List disablePadding>
              {clubStructure.map((item, index) => (
                <React.Fragment key={item.role}>
                  <ListItem sx={{ py: 1.5, px:0 }}>
                    <ListItemIcon sx={{minWidth: 40}}>{item.icon}</ListItemIcon>
                    <ListItemText
                      primary={item.role}
                      secondary={item.members.join(', ')}
                      primaryTypographyProps={{
                        fontFamily: 'comfortaa',
                        fontWeight: 'bold',
                        color: theme.palette.grey[100],
                        fontSize: '1.1rem'
                      }}
                      secondaryTypographyProps={{
                        fontFamily: 'comfortaa',
                        color: theme.palette.grey[400],
                        fontSize: '0.9rem'
                      }}
                    />
                  </ListItem>
                  {index < clubStructure.length - 1 && <Divider component="li" sx={{ borderColor: theme.palette.grey[700] }} />}
                </React.Fragment>
              ))}
            </List>
          </Paper>
        </Grid>

        {/* Kontaktformular Abschnitt */}
        <Grid item xs={12} md={7}>
           <Typography
            variant="h5"
            component="h2"
            gutterBottom
            sx={{
              fontFamily: 'comfortaa',
              fontWeight: 600,
              color: theme.palette.grey[100],
              mb: 2,
              borderBottom: `2px solid ${gelb}`,
              pb: 1,
              display: 'inline-block',
            }}
          >
            Schreib uns!
          </Typography>
          <Paper
            elevation={3}
            sx={{
              p: {xs: 2, md: 3},
              backgroundColor: 'rgba(30, 30, 30, 0.7)',
              backdropFilter: 'blur(5px)',
              borderRadius: theme.shape.borderRadius,
              border: `1px solid ${theme.palette.grey[800]}`,
            }}
          >
            <Box component="form" onSubmit={handleSubmit} noValidate>
              <Grid container spacing={2.5}>
                <Grid item xs={12}>
                  <FormControl fullWidth sx={textFieldStyles}>
                    <InputLabel id="recipient-label">An wen?</InputLabel>
                    <Select
                      labelId="recipient-label"
                      id="recipient"
                      name="recipient"
                      value={formData.recipient}
                      label="An wen?"
                      onChange={handleChange}
                      MenuProps={{
                        PaperProps: {
                          sx: {
                            backgroundColor: theme.palette.grey[800],
                            color: theme.palette.grey[200],
                            fontFamily: 'comfortaa',
                            '& .MuiMenuItem-root': {
                                fontFamily: 'comfortaa',
                                '&:hover': {
                                    backgroundColor: theme.palette.grey[700],
                                }
                            }
                          },
                        },
                      }}
                    >
                      <MenuItem value="vorstand">Vorstand</MenuItem>
                      <MenuItem value="kassenwart">Kassenwart</MenuItem>
                      <MenuItem value="spielausschuss">Spielausschuss</MenuItem>
                    </Select>
                  </FormControl>
                </Grid>
                <Grid item xs={12} sm={6}>
                  <TextField
                    required
                    fullWidth
                    id="name"
                    label="Dein Name"
                    name="name"
                    value={formData.name}
                    onChange={handleChange}
                    sx={textFieldStyles}
                  />
                </Grid>
                <Grid item xs={12} sm={6}>
                  <TextField
                    required
                    fullWidth
                    id="email"
                    label="Deine E-Mail"
                    name="email"
                    type="email"
                    value={formData.email}
                    onChange={handleChange}
                    sx={textFieldStyles}
                  />
                </Grid>
                <Grid item xs={12}>
                  <TextField
                    required
                    fullWidth
                    id="subject"
                    label="Betreff"
                    name="subject"
                    value={formData.subject}
                    onChange={handleChange}
                    sx={textFieldStyles}
                  />
                </Grid>
                <Grid item xs={12}>
                  <TextField
                    required
                    fullWidth
                    id="message"
                    label="Deine Nachricht"
                    name="message"
                    multiline
                    rows={5}
                    value={formData.message}
                    onChange={handleChange}
                    sx={textFieldStyles}
                  />
                </Grid>
                <Grid item xs={12}>
                  <Button
                    type="submit"
                    fullWidth
                    variant="contained"
                    endIcon={<SendIcon />}
                    sx={{
                      fontFamily: 'comfortaa',
                      fontWeight: 'bold',
                      py: 1.5,
                      backgroundColor: gelb, // Akzentfarbe Gelb für Button
                      color: theme.palette.getContrastText(gelb),
                      '&:hover': {
                        backgroundColor: theme.palette.warning.dark, // Dunkleres Gelb beim Hover
                      },
                    }}
                  >
                    Nachricht senden
                  </Button>
                </Grid>
              </Grid>
            </Box>
          </Paper>
        </Grid>
      </Grid>
    </Container>
  );
};

export default ContactPage;
