import { Link as RouterLink } from 'react-router-dom';
import * as React from 'react';
import { Box, Card, CardContent, Typography, CardActionArea } from '@mui/material';
import { useTheme } from '@mui/material/styles';

const NavigationCard = ({ icon, title, subtitle, link }) => {
  const theme = useTheme();

  return (
    <Card
      sx={{
        height: '100%', // Wichtig, damit Karten in einem Grid die gleiche Höhe haben
        backgroundColor: '#111', // Ein dunkler, aber nicht komplett schwarzer Hintergrund
        color: theme.palette.grey[300],
        border: `1px solid ${theme.palette.grey[800]}`,
        borderRadius: theme.shape.borderRadius,
        display: 'flex',
        flexDirection: 'column',
        // Sanfter Übergang für den Hover-Effekt
        transition: 'transform 0.3s ease-in-out, border-color 0.3s ease-in-out, box-shadow 0.3s ease-in-out',
        '&:hover': {
          transform: 'translateY(-5px)', // Hebt die Karte leicht an
          borderColor: theme.palette.error.main, // Rahmen leuchtet in Akzentfarbe
          boxShadow: `0 8px 25px -5px ${theme.palette.error.dark}`, // Schatten in Akzentfarbe
        },
      }}
    >
      {/* CardActionArea macht die gesamte Karte zu einem klickbaren Link */}
      <CardActionArea
        component={RouterLink}
        to={link}
        sx={{ 
            flexGrow: 1, // Sorgt dafür, dass der klickbare Bereich die ganze Karte ausfüllt
            display: 'flex',
            flexDirection: 'column',
            justifyContent: 'center', // Zentriert den Inhalt vertikal
            p: { xs: 2, md: 3 }, // Innenabstand
        }} 
      >
        <CardContent sx={{ display: 'flex', flexDirection: 'column', alignItems: 'center', textAlign: 'center' }}>
          
          {/* Optionales Icon */}
          {icon && (
            <Box sx={{ color: theme.palette.error.main, mb: 2 }}>
              {/* Klonen das Icon, um ihm neue Styles zu geben, ohne das Original zu verändern */}
              {React.cloneElement(icon, { sx: { fontSize: 45 } })}
            </Box>
          )}

          {/* Titel */}
          <Typography 
            variant="h5" 
            component="div" 
            gutterBottom
            sx={{ 
              fontFamily: 'comfortaa', 
              fontWeight: 700,
              color: '#fff', // Weißer, fetter Titel für starken Kontrast
            }}
          >
            {title}
          </Typography>
          
          {/* Subtitle */}
          <Typography variant="body2" sx={{ color: theme.palette.grey[400], fontFamily:'comfortaa' }}>
           {subtitle}
          </Typography>

        </CardContent>
      </CardActionArea>
    </Card>
  );
};

export default NavigationCard;
