import * as React from 'react';
import {
  Table,
  TableBody,
  TableCell,
  TableContainer,
  TableHead,
  TableRow,
  Paper,
  Box,
  Typography,
  Avatar,
  useTheme,
  Container,
  useMediaQuery,
} from '@mui/material';

// Hilfsfunktion zur Erstellung der Rekord-Daten
const createRecordData = (id, recordTitle, teamName, teamLogoColor, value) => {
  return {
    id,
    recordTitle,
    teamName,
    teamLogoColor,
    value,
  };
};

// Beispieldaten für die Rekordliste
const recordsData = [
  createRecordData(1, 'Meiste Titel', 'Dynamo Tresen', '#1976D2', 4),
  createRecordData(2, 'Meiste Siege in einer Saison', 'Rakete Freiburg', '#D32F2F', 28),
  createRecordData(3, 'Meiste Tore in einer Saison', 'Lokomotive Littenweiler', '#5D4037', 102),
  createRecordData(4, 'Wenigste Gegentore', 'Die 2. Wahl', '#757575', 19),
  createRecordData(5, 'Längste Siegesserie', 'Red Sea', '#C2185B', '15 Spiele'),
  createRecordData(6, 'Höchster Sieg', 'Wurschtwecklebombers', '#00796B', '11:0'),
];

// Eine wiederverwendbare, gestylte Tabellenzelle
const StyledTableCell = ({ children, sx, align, ...props }) => {
  const theme = useTheme();
  const isMobile = useMediaQuery(theme.breakpoints.down('sm'));

  return (
    <TableCell
      align={align}
      sx={{
        color: theme.palette.grey[300],
        fontFamily: 'comfortaa',
        borderBottom: `1px solid ${theme.palette.grey[800]}`,
        py: 1.5,
        fontSize: isMobile ? '0.75rem' : '0.9rem',
        verticalAlign: 'middle',
        ...sx,
      }}
      {...props}
    >
      {children}
    </TableCell>
  );
};

const RecordsList = ({ title }) => {
  const theme = useTheme();
  const isMobile = useMediaQuery(theme.breakpoints.down('sm'));

  return (
    <Container maxWidth="lg" sx={{ my: 4, px: isMobile ? 1 : 2 }}>
      <Typography
        variant={isMobile ? 'h5' : 'h4'}
        sx={{
          mb: 3,
          mt: 2,
          color: '#00A99D',
          fontWeight: 700,
          fontFamily: 'comfortaa',
          textAlign: 'center',
          textTransform: 'uppercase',
          letterSpacing: '0.1em',
        }}
      >
        {title}
      </Typography>
      <TableContainer
        component={Paper}
        sx={{
          backgroundColor: '#111',
          borderRadius: theme.shape.borderRadius,
          border: `1px solid ${theme.palette.grey[800]}`,
        }}
      >
        <Table aria-label="Tabelle der Rekorde">
          <TableHead>
            <TableRow sx={{ backgroundColor: 'rgba(255,255,255,0.05)' }}>
              <StyledTableCell sx={{ fontWeight: 'bold', color: theme.palette.grey[100], width: isMobile ? '40%' : '35%' }}>Rekord</StyledTableCell>
              <StyledTableCell sx={{ fontWeight: 'bold', color: theme.palette.grey[100], width: isMobile ? '40%' : '40%' }}>Team</StyledTableCell>
              <StyledTableCell align="center" sx={{ fontWeight: 'bold', color: theme.palette.grey[100], width: isMobile ? '20%' : '25%' }}>Wert</StyledTableCell>
            </TableRow>
          </TableHead>
          <TableBody>
            {recordsData.map((record) => (
              <TableRow
                key={record.id}
                sx={{ '&:hover': { backgroundColor: 'rgba(255,255,255,0.08)' } }}
              >
                <StyledTableCell component="th" scope="row">
                  <Typography sx={{ fontFamily: 'comfortaa', color: theme.palette.grey[100], fontWeight: 600, fontSize: 'inherit' }}>
                    {record.recordTitle}
                  </Typography>
                </StyledTableCell>
                <StyledTableCell>
                  <Box sx={{ display: 'flex', alignItems: 'center' }}>
                    <Avatar
                      alt={`${record.teamName} Logo`}
                      sx={{
                        width: isMobile ? 28 : 32,
                        height: isMobile ? 28 : 32,
                        mr: isMobile ? 1 : 1.5,
                        fontSize: isMobile ? '0.7rem' : '0.8rem',
                        color: theme.palette.getContrastText(record.teamLogoColor || theme.palette.grey[700]),
                        backgroundColor: record.teamLogoColor || theme.palette.grey[700],
                      }}
                    >
                      {record.teamName.substring(0, 1).toUpperCase()}
                    </Avatar>
                    <Typography noWrap sx={{ fontFamily: 'comfortaa', color: theme.palette.grey[200], fontSize: 'inherit' }}>
                      {record.teamName}
                    </Typography>
                  </Box>
                </StyledTableCell>
                <StyledTableCell align="center">
                  <Typography sx={{ fontFamily: 'comfortaa', color: theme.palette.grey[100], fontWeight: 'bold', fontSize: isMobile ? '0.8rem' : '1rem' }}>
                    {record.value}
                  </Typography>
                </StyledTableCell>
              </TableRow>
            ))}
          </TableBody>
        </Table>
      </TableContainer>
    </Container>
  );
};

export default RecordsList;
