import * as React from 'react';
import {
  Box,
  Container,
  Typography,
  TextField,
  Button,
  Grid,
  Paper,
  MenuItem,
  Select,
  FormControl,
  InputLabel,
  useTheme,
  Divider,
  List,
  ListItem,
  ListItemText,
  ListItemIcon,
  // Link, // Link wird nicht mehr für direkte Kontakte benötigt
} from '@mui/material';
import SendIcon from '@mui/icons-material/Send';
import BusinessIcon from '@mui/icons-material/Business';
import AccountBalanceWalletIcon from '@mui/icons-material/AccountBalanceWallet';
import SportsSoccerIcon from '@mui/icons-material/SportsSoccer';
// import GavelIcon from '@mui/icons-material/Gavel'; // Beispiel-Icon für Sportgericht
import LocationOnIcon from '@mui/icons-material/LocationOn';
import AssignmentIcon from '@mui/icons-material/Assignment'; // Für Ergebnisdienst
import EventSeatIcon from '@mui/icons-material/EventSeat'; // Für Platzreservierung
import GrassIcon from '@mui/icons-material/Grass'; // Für Platzpflege
import AccountBalanceIcon from '@mui/icons-material/AccountBalance'; // Für Bankverbindung
import QrCode2Icon from '@mui/icons-material/QrCode2'; // Für QR Code

// Farben
const blaugrün = '#00A99D';
const gelb = '#FFBF00';

// Daten für die Kontaktseite - E-Mails und Telefonnummern für direkte Anzeige entfernt
const contactInfo = {
  generalNote: "Kontaktadressen der einzelnen Teams findet Ihr auf der Seite Teams! Für andere Anliegen nutze bitte dieses Formular.",
  address: {
    name: "Bunte Liga Freiburg",
    person: "c/o Jonas Krause",
    street: "Ferdinand-Weiß-Straße 92",
    city: "79106 Freiburg",
  },
  otherContacts: [
    { title: "Ergebnisdienst", person: "Peter", icon: <AssignmentIcon sx={{ color: gelb }} /> },
    { title: "Platzreservierung", person: "Carlos", icon: <EventSeatIcon sx={{ color: gelb }} /> },
    { title: "Platzpflege", person: "", icon: <GrassIcon sx={{ color: gelb }} /> }, // Person kann leer bleiben, wenn nur die Abteilung genannt wird
  ],
  board: [
    { role: "1. Vorstand", name: "Jonas Krause" },
    { role: "2. Vorstand", name: "Jens Karsten" },
    { role: "Kassenwart", name: "Peter Deutschmann" },
    { role: "Kassenprüfer", name: "Klaus Becker + Valentin Czisch" },
  ],
  committee: {
    title: "Spielausschuss",
    members: "Benedikt Greiner (LokLit) und Dominik Seifer (Dynamo Tresen)",
  },
  bankDetails: {
    name: "Sparkasse Freiburg",
    iban: "DE06 6805 0101 0010 1030 69",
    bic: "FRSPDE66XXX",
    qrNote: "Oder zahl einfach per QR Code."
  }
};


const ContactSection = ({ title, icon, children }) => {
    const theme = useTheme();
    return (
        <Paper
            elevation={3}
            sx={{
                p: { xs: 2, md: 2.5 },
                mb: 3,
                backgroundColor: 'rgba(30, 30, 30, 0.7)',
                backdropFilter: 'blur(5px)',
                borderRadius: theme.shape.borderRadius,
                border: `1px solid ${theme.palette.grey[800]}`,
                width: '100%', // Stellt sicher, dass die Paper-Komponente die Breite der Grid-Spalte nutzt
            }}
        >
            <Box sx={{ display: 'flex', alignItems: 'center', mb: 1.5 }}>
                {icon && <ListItemIcon sx={{ minWidth: 36, color: gelb, mr:1 }}>{icon}</ListItemIcon>}
                <Typography variant="h6" component="h3" sx={{ fontFamily: 'comfortaa', fontWeight: 600, color: theme.palette.grey[100] }}>
                    {title}
                </Typography>
            </Box>
            {children}
        </Paper>
    );
};


const ContactPage = () => {
  const theme = useTheme();
  const [formData, setFormData] = React.useState({
    recipient: '',
    name: '',
    email: '',
    subject: '',
    message: '',
  });

  const handleChange = (event) => {
    const { name, value } = event.target;
    setFormData((prevData) => ({
      ...prevData,
      [name]: value,
    }));
  };

  const handleSubmit = (event) => {
    event.preventDefault();
    // Hier würde die Logik zum Senden des Formulars stehen
    // z.B. ein API-Aufruf
    console.log('Formulardaten:', formData);
    // Optional: Formular nach dem Senden zurücksetzen
    setFormData({
      recipient: '',
      name: '',
      email: '',
      subject: '',
      message: '',
    });
    // Optional: Erfolgsmeldung anzeigen (verwende ein besseres UI-Element als alert in einer echten App)
    alert('Nachricht gesendet (simuliert)!');
  };

  const inputLabelStyles = {
    color: theme.palette.grey[400],
    fontFamily: 'comfortaa',
    '&.Mui-focused': {
      color: gelb, // Akzentfarbe für fokussiertes Label
    },
  };

  const textFieldStyles = {
    '& .MuiInputBase-input': {
      color: theme.palette.grey[200],
      fontFamily: 'comfortaa',
    },
    '& .MuiOutlinedInput-root': {
      backgroundColor: 'rgba(0,0,0,0.1)', // Leichter Hintergrund für bessere Abhebung
      '& fieldset': {
        borderColor: theme.palette.grey[700],
      },
      '&:hover fieldset': {
        borderColor: theme.palette.grey[500],
      },
      '&.Mui-focused fieldset': {
        borderColor: gelb, // Akzentfarbe für fokussierten Rahmen
      },
    },
    '& .MuiInputLabel-root': inputLabelStyles,
  };
  
  const selectStyles = {
    ...textFieldStyles, // Übernimmt Basis-Styles
    '& .MuiSelect-select': {
        backgroundColor: 'transparent', // Verhindert doppelten Hintergrund im Select
    },
     '& .MuiSvgIcon-root': { // Farbe des Dropdown-Pfeils
        color: theme.palette.grey[400],
    }
  };


  return (
    <Container maxWidth="lg" sx={{ py: 4, color: theme.palette.grey[300] }}>
      <Typography
        variant="h3"
        component="h1"
        gutterBottom
        textAlign="center"
        sx={{ fontFamily: 'comfortaa', fontWeight: 700, color: blaugrün, mb: 3 }}
      >
        KONTAKT
      </Typography>

      <Typography
        variant="body1"
        textAlign="center"
        sx={{ fontFamily: 'comfortaa', color: theme.palette.grey[400], mb: 5 }}
      >
        {contactInfo.generalNote}
      </Typography>

      <Grid container spacing={5} justifyContent="center"> {/* Zentriert Grid Items, falls md < 12 */}
        {/* Linke Spalte: Kontaktinformationen */}
        <Grid item xs={12} md={6} lg={5}> {/* Angepasste Grid-Größen */}
          <ContactSection title="Anschrift" icon={<LocationOnIcon />}>
            <Typography variant="body2" sx={{ fontFamily: 'comfortaa', lineHeight: 1.7 }}>
              {contactInfo.address.name}<br />
              {contactInfo.address.person}<br />
              {contactInfo.address.street}<br />
              {contactInfo.address.city}
            </Typography>
          </ContactSection>

          <ContactSection title="Weitere Ansprechpartner">
             <List dense disablePadding>
                {contactInfo.otherContacts.map(contact => (
                    <ListItem key={contact.title} sx={{px:0, py:0.75}}>
                        <ListItemIcon sx={{minWidth: 36}}>{contact.icon}</ListItemIcon>
                        <ListItemText 
                            primary={contact.title + (contact.person ? ` (${contact.person})` : '')} 
                            primaryTypographyProps={{fontFamily: 'comfortaa', fontWeight:'bold', color: theme.palette.grey[200], fontSize: '1rem'}}
                        />
                    </ListItem>
                ))}
             </List>
          </ContactSection>

          <ContactSection title="Vorstand" icon={<BusinessIcon />}>
            <List dense disablePadding>
              {contactInfo.board.map(member => (
                <ListItem key={member.role} sx={{px:0, py:0.5, display:'block'}}>
                  <Typography variant="subtitle2" sx={{fontFamily: 'comfortaa', fontWeight:'bold', color: theme.palette.grey[100]}}>{member.role}:</Typography>
                  <Typography variant="body2" sx={{fontFamily: 'comfortaa', color: theme.palette.grey[300], pl:2}}>{member.name}</Typography>
                </ListItem>
              ))}
            </List>
          </ContactSection>

           <ContactSection title={contactInfo.committee.title} icon={<SportsSoccerIcon />}>
                <Typography variant="body2" sx={{fontFamily: 'comfortaa', color: theme.palette.grey[300]}}>{contactInfo.committee.members}</Typography>
           </ContactSection>

            <ContactSection title="Bankverbindung" icon={<AccountBalanceIcon />}>
                <Typography variant="body2" sx={{fontFamily: 'comfortaa', lineHeight: 1.7}}>
                    {contactInfo.bankDetails.name}<br/>
                    IBAN: {contactInfo.bankDetails.iban}<br/>
                    BIC: {contactInfo.bankDetails.bic}
                </Typography>
                <Typography variant="caption" sx={{fontFamily: 'comfortaa', color: theme.palette.grey[500], display:'flex', alignItems:'center', mt:1}}>
                   <QrCode2Icon sx={{fontSize: '1.1rem', mr:0.5}}/> {contactInfo.bankDetails.qrNote}
                </Typography>
            </ContactSection>

        </Grid>

        {/* Rechte Spalte: Kontaktformular */}
        <Grid item xs={12} md={6} lg={7}> {/* Angepasste Grid-Größen */}
           <Typography
            variant="h5"
            component="h2"
            gutterBottom
            sx={{
              fontFamily: 'comfortaa',
              fontWeight: 600,
              color: theme.palette.grey[100],
              mb: 2,
              borderBottom: `2px solid ${gelb}`,
              pb: 1,
              display: 'inline-block',
            }}
          >
            Schreib uns!
          </Typography>
          <Paper
            elevation={3}
            sx={{
              p: {xs: 2, md: 3},
              backgroundColor: 'rgba(30, 30, 30, 0.7)',
              backdropFilter: 'blur(5px)',
              borderRadius: theme.shape.borderRadius,
              border: `1px solid ${theme.palette.grey[800]}`,
            }}
          >
            <Box component="form" onSubmit={handleSubmit} noValidate>
              <Grid container spacing={2}> {/* Leichter reduzierter Abstand zwischen Formularfeldern */}
                <Grid item xs={12}>
                  <FormControl fullWidth sx={selectStyles}>
                    <InputLabel id="recipient-label">An wen?</InputLabel>
                    <Select
                      labelId="recipient-label"
                      id="recipient"
                      name="recipient"
                      value={formData.recipient}
                      label="An wen?"
                      onChange={handleChange}
                      MenuProps={{
                        PaperProps: {
                          sx: {
                            backgroundColor: theme.palette.grey[900], // Dunkler Hintergrund für Dropdown-Menü
                            color: theme.palette.grey[200],
                            fontFamily: 'comfortaa',
                            border: `1px solid ${theme.palette.grey[700]}`,
                            '& .MuiMenuItem-root': {
                                fontFamily: 'comfortaa',
                                '&:hover': {
                                    backgroundColor: theme.palette.grey[800],
                                },
                                '&.Mui-selected': { // Style für ausgewähltes Item
                                    backgroundColor: 'rgba(255, 191, 0, 0.1)', // Leichter gelber Hintergrund
                                    '&:hover': {
                                       backgroundColor: 'rgba(255, 191, 0, 0.2)',
                                    }
                                }
                            }
                          },
                        },
                      }}
                    >
                      <MenuItem value="" disabled><em>Bitte auswählen</em></MenuItem>
                      <MenuItem value="vorstand">Vorstand</MenuItem>
                      <MenuItem value="kassenwart">Kassenwart</MenuItem>
                      <MenuItem value="spielausschuss">Spielausschuss</MenuItem>
                      <MenuItem value="ergebnisdienst">Ergebnisdienst</MenuItem>
                      <MenuItem value="platzreservierung">Platzreservierung/Platzpflege</MenuItem>
                      <MenuItem value="sonstiges">Sonstiges</MenuItem>
                    </Select>
                  </FormControl>
                </Grid>
                <Grid item xs={12} sm={6}>
                  <TextField
                    required
                    fullWidth
                    id="name"
                    label="Dein Name"
                    name="name"
                    value={formData.name}
                    onChange={handleChange}
                    sx={textFieldStyles}
                  />
                </Grid>
                <Grid item xs={12} sm={6}>
                  <TextField
                    required
                    fullWidth
                    id="email"
                    label="Deine E-Mail"
                    name="email"
                    type="email"
                    value={formData.email}
                    onChange={handleChange}
                    sx={textFieldStyles}
                  />
                </Grid>
                <Grid item xs={12}>
                  <TextField
                    required
                    fullWidth
                    id="subject"
                    label="Betreff"
                    name="subject"
                    value={formData.subject}
                    onChange={handleChange}
                    sx={textFieldStyles}
                  />
                </Grid>
                <Grid item xs={12}>
                  <TextField
                    required
                    fullWidth
                    id="message"
                    label="Deine Nachricht"
                    name="message"
                    multiline
                    rows={5}
                    value={formData.message}
                    onChange={handleChange}
                    sx={textFieldStyles}
                  />
                </Grid>
                <Grid item xs={12}>
                  <Button
                    type="submit"
                    fullWidth
                    variant="contained"
                    endIcon={<SendIcon />}
                    sx={{
                      fontFamily: 'comfortaa',
                      fontWeight: 'bold',
                      py: 1.5,
                      backgroundColor: gelb, 
                      color: theme.palette.getContrastText(gelb), // Stellt sicher, dass Text lesbar ist
                      '&:hover': {
                        backgroundColor: theme.palette.warning.dark, 
                      },
                    }}
                  >
                    Nachricht senden
                  </Button>
                </Grid>
              </Grid>
            </Box>
          </Paper>
        </Grid>
      </Grid>
    </Container>
  );
};

export default ContactPage;
