import * as React from 'react';
import AppBar from '@mui/material/AppBar';
import Box from '@mui/material/Box';
import Toolbar from '@mui/material/Toolbar';
import IconButton from '@mui/material/IconButton';
import Typography from '@mui/material/Typography';
import MenuIcon from '@mui/icons-material/Menu';
import Container from '@mui/material/Container';
import Button from '@mui/material/Button';
import AdbIcon from '@mui/icons-material/Adb';
import Drawer from '@mui/material/Drawer';
import ChevronRightIcon from '@mui/icons-material/ChevronRight';
import ChevronLeftIcon from '@mui/icons-material/ChevronLeft';
import ListItem from '@mui/material/ListItem';
import ListItemButton from '@mui/material/ListItemButton';
import ListItemIcon from '@mui/material/ListItemIcon';
import ListItemText from '@mui/material/ListItemText';
import Divider from '@mui/material/Divider';
import List from '@mui/material/List';
import { styled, useTheme } from '@mui/material/styles';

import HomeIcon from '@mui/icons-material/Home';
import TableViewIcon from '@mui/icons-material/TableView';
import Diversity3Icon from '@mui/icons-material/Diversity3';
import ScheduleIcon from '@mui/icons-material/Schedule';
import PermContactCalendarIcon from '@mui/icons-material/PermContactCalendar';
import ListIcon from '@mui/icons-material/List';
import { useNavigate } from 'react-router-dom';
import logo from '../img/logo.png';

const pages = [
  { text: 'Home', iconName: 'HomeIcon', path: '/' },
  { text: 'Ergebnisse', iconName: 'TableViewIcon', path: '/ergebnisse' },
  { text: 'Teams', iconName: 'Diversity3Icon', path: '/teams' },
  { text: 'Historiker-Ecke', iconName: 'ScheduleIcon', path: '/historie' },
  { text: 'Kontakt', iconName: 'PermContactCalendarIcon', path: '/kontakt' },
];

const iconMap = {
  HomeIcon: HomeIcon,
  TableViewIcon: TableViewIcon,
  Diversity3Icon: Diversity3Icon,
  ScheduleIcon: ScheduleIcon,
  PermContactCalendarIcon: PermContactCalendarIcon,
  DefaultIcon: ListIcon,
};

const drawerWidth = 240;

// GEÄNDERT: Definieren Sie die Größen als Konstanten für einfache Anpassung
const initialLogoHeight = 40;
const shrunkLogoHeight = 30;
const initialLogoWidth = 50;
const shrunkLogoWidth = 40;

const CustomDrawerHeader = styled('div')(({ theme }) => ({
  display: 'flex',
  alignItems: 'center',
  padding: theme.spacing(0, 1),
  ...theme.mixins.toolbar,
  justifyContent: 'flex-start',
}));

function Header() {
  let navigate = useNavigate();
  const theme = useTheme();
  const [mobileOpen, setMobileOpen] = React.useState(false);

  // NEU: State, um den Scroll-Zustand zu verfolgen
  const [isShrunk, setIsShrunk] = React.useState(false);

  // NEU: useEffect, um den Scroll-Listener hinzuzufügen und zu entfernen
  React.useEffect(() => {
    const handleScroll = () => {
      // Setzt isShrunk auf true, wenn mehr als 50px gescrollt wurde
      if (window.scrollY > 50) {
        setIsShrunk(true);
      } else {
        setIsShrunk(false);
      }
    };

    window.addEventListener('scroll', handleScroll);

    // Wichtig: Den Listener entfernen, wenn die Komponente unmounted wird
    return () => {
      window.removeEventListener('scroll', handleScroll);
    };
  }, []); // Leeres Array sorgt dafür, dass der Effekt nur einmal ausgeführt wird

  const handleDrawerToggle = () => {
    setMobileOpen(!mobileOpen);
  };
  
  const drawerContent = (
      <Box
        sx={{
          width: drawerWidth,
          backgroundColor: theme.palette.grey[800],
          color: theme.palette.common.white,
          height: '100%',
        }}
        onClick={handleDrawerToggle}
        onKeyDown={handleDrawerToggle}
      >
        <CustomDrawerHeader sx={{ justifyContent: 'flex-end' }}>
          <IconButton onClick={handleDrawerToggle} sx={{ color: theme.palette.common.white }}>
            {theme.direction === 'rtl' ? <ChevronLeftIcon /> : <ChevronRightIcon />}
          </IconButton>
        </CustomDrawerHeader>
        <Divider sx={{ borderColor: theme.palette.grey[700] }} />
        <List>
          {pages.map((pageItem) => {
            const IconComponent = iconMap[pageItem.iconName] || iconMap.DefaultIcon;
            return (
              <ListItem key={pageItem.text} disablePadding>
                <ListItemButton
                  onClick={() => { navigate(pageItem.path); handleDrawerToggle(); }}
                >
                  <ListItemIcon sx={{ color: theme.palette.common.white }}>
                    {IconComponent ? <IconComponent /> : null}
                  </ListItemIcon>
                  <ListItemText primary={pageItem.text} />
                </ListItemButton>
              </ListItem>
            );
          })}
        </List>
      </Box>
  );

  return (
    // GEÄNDERT: `position="sticky"` ist perfekt hierfür
    <AppBar position="sticky" sx={{ backgroundColor: '#000' }}>
      <Container maxWidth="xl">
        {/* GEÄNDERT: Die Toolbar erhält die bedingte Höhe und die Transition */}
        <Toolbar 
          disableGutters 
          sx={{ 
            // Die minHeight ändert sich basierend auf dem `isShrunk`-State
            // MUI's Standardhöhe für Toolbars ist 56px (mobile) und 64px (desktop)
            minHeight: { md: isShrunk ? 50 : 70 }, 
            // Fügt eine sanfte Transition für die Höhenänderung hinzu
            transition: theme.transitions.create(['min-height'], {
              duration: theme.transitions.duration.short,
            }),
          }}
        >

          <Box sx={{ display: { xs: 'none', md: 'flex' }, mr: 1, alignItems: 'center' }}>
            <img
              src={logo} 
              alt="Bunte Liga Freiburg Logo"
              // GEÄNDERT: Dynamische Höhe und Breite basierend auf `isShrunk`
              style={{ 
                height: isShrunk ? `${shrunkLogoHeight}px` : `${initialLogoHeight}px`, 
                width: isShrunk ? `${shrunkLogoWidth}px` : `${initialLogoWidth}px`, 
                objectFit: 'contain',
                // Fügt auch hier eine Transition hinzu für ein sanftes Schrumpfen
                transition: 'height 0.3s ease-in-out, width 0.3s ease-in-out',
              }}
              onError={(e) => { e.currentTarget.src = `https://placehold.co/${initialLogoWidth}x${initialLogoHeight}/eeeeee/222222?text=Logo+Error`; e.currentTarget.alt = 'Fehler beim Laden des Logos'; }}
            />
          </Box>
          <Typography
            variant="h6"
            noWrap
            component="a"
            href="/"
            sx={{
              mr: 2,
              display: { xs: 'none', md: 'flex' },
              fontFamily: 'monospace',
              fontWeight: 700,
              letterSpacing: '.3rem',
              color: 'inherit',
              textDecoration: 'none',
              alignItems: 'center'
            }}
          >
            DIE BUNTE LIGA
          </Typography>
          
          {/* Mobile Header */}
          <Box sx={{ flexGrow: 1, display: { xs: 'flex', md: 'none' }, alignItems: 'center', justifyContent: 'flex-start' }}>
            <Box sx={{ display: 'flex', alignItems: 'center', mx: 1 }} component="a" href="/" color="inherit" underline="none">
                <img
                  src={logo} 
                  alt="Bunte Liga Freiburg Logo Mobile"
                  // GEÄNDERT: Mobile hat immer die gleiche Größe, da die Toolbar-Höhe sich hier nicht ändert
                  style={{ height: `${initialLogoHeight}px`, width: `${initialLogoWidth}px`, objectFit: 'contain' }}
                />
            </Box>
            <Typography
              variant="h6" 
              noWrap
              component="a"
              href="/"
              sx={{
                display: 'flex', 
                fontFamily: 'monospace',
                fontWeight: 800,
                letterSpacing: '0.1rem',
                color: 'inherit',
                textDecoration: 'none',
                alignItems: 'center'
              }}
            >
              DIE BUNTE LIGA
            </Typography>
          </Box>
          <Box sx={{ display: { xs: 'flex', md: 'none' }, alignItems: 'center' }}>
            <IconButton
              size="large"
              aria-label="open navigation menu"
              onClick={handleDrawerToggle}
              color="inherit"
            >
              <MenuIcon />
            </IconButton>
          </Box>
          
          {/* Drawer */}
          <Drawer
            variant="temporary"
            anchor="right"
            open={mobileOpen}
            onClose={handleDrawerToggle}
            ModalProps={{
              keepMounted: true,
            }}
            sx={{
              display: { xs: 'block', md: 'none' },
              '& .MuiDrawer-paper': {
                boxSizing: 'border-box',
                width: drawerWidth,
              },
            }}
          >
            {drawerContent}
          </Drawer>

          {/* Desktop Navigation Buttons */}
          <Box sx={{ flexGrow: 1, display: { xs: 'none', md: 'flex' }, justifyContent: 'flex-end' }}>
            {pages.map((pageItem) => (
              <Button
                key={pageItem.text}
                // GEÄNDERT: Reduziert das vertikale Padding (my) beim Scrollen
                sx={{ 
                  my: 0.5, 
                  color: 'white', 
                  display: 'block', 
                  '&:hover': { backgroundColor: 'rgba(255,255,255,0.1)' },
                  transition: theme.transitions.create(['margin-top', 'margin-bottom'], {
                    duration: theme.transitions.duration.short,
                  }),
                }}
                className="rounded-md"
                onClick={() => { navigate(pageItem.path);}}
              >
                {pageItem.text}
              </Button>
            ))}
          </Box>
        </Toolbar>
      </Container>
    </AppBar>
  );
}
export default Header;
