import * as React from 'react';
import {
  Box,
  Container,
  Typography,
  TextField,
  Button,
  Grid,
  Paper,
  MenuItem,
  Select,
  FormControl,
  InputLabel,
  useTheme,
  Divider,
  List,
  ListItem,
  ListItemText,
  ListItemIcon,
  Link,
  Icon, // Für generische Icons, falls benötigt
} from '@mui/material';
import SendIcon from '@mui/icons-material/Send';
import BusinessIcon from '@mui/icons-material/Business';
import AccountBalanceWalletIcon from '@mui/icons-material/AccountBalanceWallet';
import SportsSoccerIcon from '@mui/icons-material/SportsSoccer';
import GavelIcon from '@mui/icons-material/Gavel'; // Für Sportgericht (Beispiel)
import EmailIcon from '@mui/icons-material/Email';
import PhoneIcon from '@mui/icons-material/Phone';
import LocationOnIcon from '@mui/icons-material/LocationOn';
import AssignmentIcon from '@mui/icons-material/Assignment'; // Für Ergebnisdienst
import EventSeatIcon from '@mui/icons-material/EventSeat'; // Für Platzreservierung
import GrassIcon from '@mui/icons-material/Grass'; // Für Platzpflege
import AccountBalanceIcon from '@mui/icons-material/AccountBalance'; // Für Bankverbindung
import QrCode2Icon from '@mui/icons-material/QrCode2'; // Für QR Code

// Farben
const blaugrün = '#00A99D';
const gelb = '#FFBF00';

// Daten für die Kontaktseite
const contactInfo = {
  generalNote: "Kontaktadressen der einzelnen Teams findet Ihr auf der Seite Teams!",
  address: {
    name: "Bunte Liga Freiburg",
    person: "c/o Jonas Krause",
    street: "Ferdinand-Weiß-Straße 92",
    city: "79106 Freiburg",
  },
  otherContacts: [
    { title: "Ergebnisdienst", person: "Peter", email: "ergebnisse@bunteligafreiburg.de", icon: <AssignmentIcon sx={{ color: gelb }} /> },
    { title: "Platzreservierung", person: "Carlos", email: "plaetze@bunteligafreiburg.de", icon: <EventSeatIcon sx={{ color: gelb }} /> },
    { title: "Platzpflege", person: "", email: "rasenteam@bunteligafreiburg.de", icon: <GrassIcon sx={{ color: gelb }} /> },
  ],
  board: [
    { role: "1. Vorstand", name: "Jonas Krause", phone: "0157-89505131", email: "vorstand@bunteligafreiburg.de" },
    { role: "2. Vorstand", name: "Jens Karsten", phone: "0761-7072963", email: "vorstand@bunteligafreiburg.de" }, // Annahme: gleiche E-Mail für Vorstand
    { role: "Kassenwart", name: "Peter Deutschmann", phone: "0761-6800260", email: "kassenwart@bunteligafreiburg.de" },
    { role: "Kassenprüfer", name: "Klaus Becker + Valentin Czisch", phone: null, email: null },
  ],
  committee: {
    title: "Spielausschuss",
    members: "Benedikt Greiner (LokLit) und Dominik Seifer (Dynamo Tresen)",
    email: "spielausschuss@bunteligafreiburg.de",
  },
  bankDetails: {
    name: "Sparkasse Freiburg",
    iban: "DE06 6805 0101 0010 1030 69",
    bic: "FRSPDE66XXX",
    qrNote: "Oder zahl einfach per QR Code."
  }
};


const ContactSection = ({ title, icon, children }) => {
    const theme = useTheme();
    return (
        <Paper
            elevation={3}
            sx={{
                p: { xs: 2, md: 2.5 },
                mb: 3,
                backgroundColor: 'rgba(30, 30, 30, 0.7)',
                backdropFilter: 'blur(5px)',
                borderRadius: theme.shape.borderRadius,
                border: `1px solid ${theme.palette.grey[800]}`,
            }}
        >
            <Box sx={{ display: 'flex', alignItems: 'center', mb: 1.5 }}>
                {icon && <ListItemIcon sx={{ minWidth: 36, color: gelb, mr:1 }}>{icon}</ListItemIcon>}
                <Typography variant="h6" component="h3" sx={{ fontFamily: 'comfortaa', fontWeight: 600, color: theme.palette.grey[100] }}>
                    {title}
                </Typography>
            </Box>
            {children}
        </Paper>
    );
};


const ContactPage = () => {
  const theme = useTheme();
  const [formData, setFormData] = React.useState({
    recipient: '',
    name: '',
    email: '',
    subject: '',
    message: '',
  });

  const handleChange = (event) => {
    const { name, value } = event.target;
    setFormData((prevData) => ({
      ...prevData,
      [name]: value,
    }));
  };

  const handleSubmit = (event) => {
    event.preventDefault();
    console.log('Formulardaten:', formData);
    setFormData({ recipient: '', name: '', email: '', subject: '', message: '' });
    alert('Nachricht gesendet (simuliert)!');
  };

  const inputLabelStyles = {
    color: theme.palette.grey[400],
    fontFamily: 'comfortaa',
    '&.Mui-focused': { color: gelb },
  };

  const textFieldStyles = {
    '& .MuiInputBase-input': { color: theme.palette.grey[200], fontFamily: 'comfortaa' },
    '& .MuiOutlinedInput-root': {
      '& fieldset': { borderColor: theme.palette.grey[700] },
      '&:hover fieldset': { borderColor: theme.palette.grey[500] },
      '&.Mui-focused fieldset': { borderColor: gelb },
    },
    '& .MuiInputLabel-root': inputLabelStyles,
  };

  return (
    <Container maxWidth="lg" sx={{ py: 4, color: theme.palette.grey[300] }}>
      <Typography
        variant="h3"
        component="h1"
        gutterBottom
        textAlign="center"
        sx={{ fontFamily: 'comfortaa', fontWeight: 700, color: blaugrün, mb: 5 }}
      >
        KONTAKT
      </Typography>

      <Typography
        variant="body1"
        textAlign="center"
        sx={{ fontFamily: 'comfortaa', color: theme.palette.grey[400], mb: 4 }}
      >
        {contactInfo.generalNote}
      </Typography>

      <Grid container spacing={5}>
        {/* Linke Spalte: Kontaktinformationen */}
        <Grid item xs={12} md={5}>
          <ContactSection title="Anschrift" icon={<LocationOnIcon />}>
            <Typography variant="body2" sx={{ fontFamily: 'comfortaa' }}>
              {contactInfo.address.name}<br />
              {contactInfo.address.person}<br />
              {contactInfo.address.street}<br />
              {contactInfo.address.city}
            </Typography>
          </ContactSection>

          <ContactSection title="Weitere Kontakte">
             <List dense disablePadding>
                {contactInfo.otherContacts.map(contact => (
                    <ListItem key={contact.title} sx={{px:0, py:0.5}}>
                        <ListItemIcon sx={{minWidth: 36}}>{contact.icon}</ListItemIcon>
                        <ListItemText 
                            primary={contact.title + (contact.person ? ` (${contact.person})` : '')} 
                            secondary={<Link href={`mailto:${contact.email}`} sx={{color: theme.palette.grey[500], '&:hover': {color: gelb}}}>{contact.email}</Link>}
                            primaryTypographyProps={{fontFamily: 'comfortaa', fontWeight:'bold', color: theme.palette.grey[200]}}
                            secondaryTypographyProps={{fontFamily: 'comfortaa'}}
                        />
                    </ListItem>
                ))}
             </List>
          </ContactSection>

          <ContactSection title="Vorstand" icon={<BusinessIcon />}>
            <List dense disablePadding>
              {contactInfo.board.map(member => (
                <ListItem key={member.role} sx={{px:0, py:0.5, display:'block'}}>
                  <Typography variant="subtitle2" sx={{fontFamily: 'comfortaa', fontWeight:'bold', color: theme.palette.grey[100]}}>{member.role}:</Typography>
                  <Typography variant="body2" sx={{fontFamily: 'comfortaa', color: theme.palette.grey[300], pl:2}}>{member.name}</Typography>
                  {member.phone && (
                    <Link href={`tel:${member.phone}`} sx={{fontFamily: 'comfortaa', color: theme.palette.grey[500], '&:hover': {color: gelb}, display:'block', pl:2, fontSize: '0.8rem'}}>
                        <PhoneIcon sx={{fontSize: '0.9rem', verticalAlign: 'middle', mr: 0.5}}/> {member.phone}
                    </Link>
                  )}
                  {member.email && (
                     <Link href={`mailto:${member.email}`} sx={{fontFamily: 'comfortaa', color: theme.palette.grey[500], '&:hover': {color: gelb}, display:'block', pl:2, fontSize: '0.8rem'}}>
                        <EmailIcon sx={{fontSize: '0.9rem', verticalAlign: 'middle', mr: 0.5}}/> {member.email}
                    </Link>
                  )}
                </ListItem>
              ))}
            </List>
          </ContactSection>

           <ContactSection title={contactInfo.committee.title} icon={<SportsSoccerIcon />}>
                <Typography variant="body2" sx={{fontFamily: 'comfortaa', color: theme.palette.grey[300]}}>{contactInfo.committee.members}</Typography>
                <Link href={`mailto:${contactInfo.committee.email}`} sx={{fontFamily: 'comfortaa', color: theme.palette.grey[500], '&:hover': {color: gelb}, display:'block', fontSize: '0.8rem', mt:0.5}}>
                    <EmailIcon sx={{fontSize: '0.9rem', verticalAlign: 'middle', mr: 0.5}}/> {contactInfo.committee.email}
                </Link>
           </ContactSection>

            <ContactSection title="Bankverbindung" icon={<AccountBalanceIcon />}>
                <Typography variant="body2" sx={{fontFamily: 'comfortaa'}}>
                    {contactInfo.bankDetails.name}<br/>
                    IBAN: {contactInfo.bankDetails.iban}<br/>
                    BIC: {contactInfo.bankDetails.bic}
                </Typography>
                <Typography variant="caption" sx={{fontFamily: 'comfortaa', color: theme.palette.grey[500], display:'flex', alignItems:'center', mt:1}}>
                   <QrCode2Icon sx={{fontSize: '1.1rem', mr:0.5}}/> {contactInfo.bankDetails.qrNote}
                </Typography>
            </ContactSection>

        </Grid>

        {/* Rechte Spalte: Kontaktformular */}
        <Grid item xs={12} md={7}>
           <Typography
            variant="h5"
            component="h2"
            gutterBottom
            sx={{
              fontFamily: 'comfortaa',
              fontWeight: 600,
              color: theme.palette.grey[100],
              mb: 2,
              borderBottom: `2px solid ${gelb}`,
              pb: 1,
              display: 'inline-block',
            }}
          >
            Schreib uns!
          </Typography>
          <Paper
            elevation={3}
            sx={{
              p: {xs: 2, md: 3},
              backgroundColor: 'rgba(30, 30, 30, 0.7)',
              backdropFilter: 'blur(5px)',
              borderRadius: theme.shape.borderRadius,
              border: `1px solid ${theme.palette.grey[800]}`,
            }}
          >
            <Box component="form" onSubmit={handleSubmit} noValidate>
              <Grid container spacing={2.5}>
                <Grid item xs={12}>
                  <FormControl fullWidth sx={textFieldStyles}>
                    <InputLabel id="recipient-label">An wen?</InputLabel>
                    <Select
                      labelId="recipient-label"
                      id="recipient"
                      name="recipient"
                      value={formData.recipient}
                      label="An wen?"
                      onChange={handleChange}
                      MenuProps={{
                        PaperProps: {
                          sx: {
                            backgroundColor: theme.palette.grey[800],
                            color: theme.palette.grey[200],
                            fontFamily: 'comfortaa',
                            '& .MuiMenuItem-root': {
                                fontFamily: 'comfortaa',
                                '&:hover': {
                                    backgroundColor: theme.palette.grey[700],
                                }
                            }
                          },
                        },
                      }}
                    >
                      <MenuItem value="vorstand">Vorstand</MenuItem>
                      <MenuItem value="kassenwart">Kassenwart</MenuItem>
                      <MenuItem value="spielausschuss">Spielausschuss</MenuItem>
                       {/* Weitere Empfänger können hier hinzugefügt werden */}
                    </Select>
                  </FormControl>
                </Grid>
                <Grid item xs={12} sm={6}>
                  <TextField
                    required
                    fullWidth
                    id="name"
                    label="Dein Name"
                    name="name"
                    value={formData.name}
                    onChange={handleChange}
                    sx={textFieldStyles}
                  />
                </Grid>
                <Grid item xs={12} sm={6}>
                  <TextField
                    required
                    fullWidth
                    id="email"
                    label="Deine E-Mail"
                    name="email"
                    type="email"
                    value={formData.email}
                    onChange={handleChange}
                    sx={textFieldStyles}
                  />
                </Grid>
                <Grid item xs={12}>
                  <TextField
                    required
                    fullWidth
                    id="subject"
                    label="Betreff"
                    name="subject"
                    value={formData.subject}
                    onChange={handleChange}
                    sx={textFieldStyles}
                  />
                </Grid>
                <Grid item xs={12}>
                  <TextField
                    required
                    fullWidth
                    id="message"
                    label="Deine Nachricht"
                    name="message"
                    multiline
                    rows={5}
                    value={formData.message}
                    onChange={handleChange}
                    sx={textFieldStyles}
                  />
                </Grid>
                <Grid item xs={12}>
                  <Button
                    type="submit"
                    fullWidth
                    variant="contained"
                    endIcon={<SendIcon />}
                    sx={{
                      fontFamily: 'comfortaa',
                      fontWeight: 'bold',
                      py: 1.5,
                      backgroundColor: gelb, 
                      color: theme.palette.getContrastText(gelb),
                      '&:hover': {
                        backgroundColor: theme.palette.warning.dark, 
                      },
                    }}
                  >
                    Nachricht senden
                  </Button>
                </Grid>
              </Grid>
            </Box>
          </Paper>
        </Grid>
      </Grid>
    </Container>
  );
};

export default ContactPage;
