import * as React from 'react';
import AppBar from '@mui/material/AppBar';
import Box from '@mui/material/Box';
import Toolbar from '@mui/material/Toolbar';
import IconButton from '@mui/material/IconButton';
import Typography from '@mui/material/Typography';
import MenuIcon from '@mui/icons-material/Menu';
import Container from '@mui/material/Container';
import Button from '@mui/material/Button';
import Drawer from '@mui/material/Drawer';
import ChevronRightIcon from '@mui/icons-material/ChevronRight';
import ChevronLeftIcon from '@mui/icons-material/ChevronLeft';
import ListItem from '@mui/material/ListItem';
import ListItemButton from '@mui/material/ListItemButton';
import ListItemIcon from '@mui/material/ListItemIcon';
import ListItemText from '@mui/material/ListItemText';
import Divider from '@mui/material/Divider';
import List from '@mui/material/List';
import { styled, useTheme } from '@mui/material/styles';

import HomeIcon from '@mui/icons-material/Home';
import TableViewIcon from '@mui/icons-material/TableView';
import Diversity3Icon from '@mui/icons-material/Diversity3';
import ScheduleIcon from '@mui/icons-material/Schedule';
import PermContactCalendarIcon from '@mui/icons-material/PermContactCalendar';
import ListIcon from '@mui/icons-material/List';
import { useNavigate } from 'react-router-dom';
import logo from '../img/logo.png';

const pages = [
  { text: 'Home', iconName: 'HomeIcon', path: '/' },
  { text: 'Ergebnisse', iconName: 'TableViewIcon', path: '/ergebnisse' },
  { text: 'Teams', iconName: 'Diversity3Icon', path: '/teams' },
  { text: 'Historiker-Ecke', iconName: 'ScheduleIcon', path: '/historie' },
  { text: 'Kontakt', iconName: 'PermContactCalendarIcon', path: '/kontakt' },
];

const iconMap = {
  HomeIcon: HomeIcon,
  TableViewIcon: TableViewIcon,
  Diversity3Icon: Diversity3Icon,
  ScheduleIcon: ScheduleIcon,
  PermContactCalendarIcon: PermContactCalendarIcon,
  DefaultIcon: ListIcon,
};

const drawerWidth = 240;
const initialLogoHeight = 40;
const shrunkLogoHeight = 30;
const initialLogoWidth = 50;
const shrunkLogoWidth = 40;
const mobileInitialLogoHeight = 35;
const mobileShrunkLogoHeight = 28;
const mobileInitialLogoWidth = 45;
const mobileShrunkLogoWidth = 38;


const CustomDrawerHeader = styled('div')(({ theme }) => ({
  display: 'flex',
  alignItems: 'center',
  padding: theme.spacing(0, 1),
  ...theme.mixins.toolbar,
  justifyContent: 'flex-start',
}));

function Header() {
  let navigate = useNavigate();
  const theme = useTheme();
  const [mobileOpen, setMobileOpen] = React.useState(false);
  const [isShrunk, setIsShrunk] = React.useState(false);

  React.useEffect(() => {
    const handleScroll = () => {
      if (window.scrollY > 50) {
        setIsShrunk(true);
      } else {
        setIsShrunk(false);
      }
    };
    window.addEventListener('scroll', handleScroll);
    return () => {
      window.removeEventListener('scroll', handleScroll);
    };
  }, []);

  const handleDrawerToggle = () => {
    setMobileOpen(!mobileOpen);
  };
  
  const drawerContent = (
      <Box
        sx={{
          width: drawerWidth,
          backgroundColor: theme.palette.grey[800],
          color: theme.palette.common.white,
          height: '100%',
        }}
        onClick={handleDrawerToggle}
        onKeyDown={handleDrawerToggle}
      >
        <CustomDrawerHeader sx={{ justifyContent: 'flex-end' }}>
          <IconButton onClick={handleDrawerToggle} sx={{ color: theme.palette.common.white }}>
            {theme.direction === 'rtl' ? <ChevronLeftIcon /> : <ChevronRightIcon />}
          </IconButton>
        </CustomDrawerHeader>
        <Divider sx={{ borderColor: theme.palette.grey[700] }} />
        <List>
          {pages.map((pageItem) => {
            const IconComponent = iconMap[pageItem.iconName] || iconMap.DefaultIcon;
            return (
              <ListItem key={pageItem.text} disablePadding>
                <ListItemButton
                  onClick={() => { navigate(pageItem.path); handleDrawerToggle(); }}
                >
                  <ListItemIcon sx={{ color: theme.palette.common.white }}>
                    {IconComponent ? <IconComponent /> : null}
                  </ListItemIcon>
                  <ListItemText primary={pageItem.text} />
                </ListItemButton>
              </ListItem>
            );
          })}
        </List>
      </Box>
  );

  return (
    <AppBar position="sticky" sx={{ backgroundColor: '#000' }}>
      <Container maxWidth="xl">
        <Toolbar 
          disableGutters 
          sx={{ 
            minHeight: { 
                xs: isShrunk ? 40 : 60, 
                md: isShrunk ? 40 : 70 
            }, 
            transition: theme.transitions.create(['min-height'], {
              duration: theme.transitions.duration.short,
            }),
          }}
        >

          {/* --- Desktop Ansicht --- */}
          <Box sx={{ display: { xs: 'none', md: 'flex' }, mr: 1, alignItems: 'center' }}>
            <img
              src={logo} 
              alt="Bunte Liga Freiburg Logo"
              style={{ 
                height: isShrunk ? `${shrunkLogoHeight}px` : `${initialLogoHeight}px`, 
                width: isShrunk ? `${shrunkLogoWidth}px` : `${initialLogoWidth}px`, 
                objectFit: 'contain',
                transition: 'height 0.3s ease-in-out, width 0.3s ease-in-out',
              }}
            />
          </Box>
          <Typography
            variant="h6"
            noWrap
            component="a"
            href="/"
            sx={{
              mr: 2,
              display: { xs: 'none', md: 'flex' },
              fontFamily: 'monospace',
              fontWeight: 700,
              letterSpacing: '.3rem',
              color: 'inherit',
              textDecoration: 'none',
              alignItems: 'center'
            }}
          >
            DIE BUNTE LIGA
          </Typography>
          
          {/* --- Mobile Ansicht --- */}
          <Box sx={{ flexGrow: 1, display: { xs: 'flex', md: 'none' }, alignItems: 'center', justifyContent: 'flex-start' }}>
            <Box sx={{ display: 'flex', alignItems: 'center', mx: 1 }} component="a" href="/" color="inherit" underline="none">
                <img
                  src={logo} 
                  alt="Bunte Liga Freiburg Logo Mobile"
                  style={{ 
                    height: `${isShrunk ? mobileShrunkLogoHeight : mobileInitialLogoHeight}px`, 
                    width: `${isShrunk ? mobileShrunkLogoWidth : mobileInitialLogoWidth}px`, 
                    objectFit: 'contain',
                    transition: 'height 0.3s ease-in-out, width 0.3s ease-in-out',
                  }}
                />
            </Box>
            <Typography
              variant={isShrunk ? 'subtitle1' : 'h6' }
              noWrap
              component="a"
              href="/"
              sx={{
                display: 'flex', 
                fontFamily: 'monospace',
                fontWeight: 700,
                letterSpacing: '0.05rem',
                color: 'inherit',
                textDecoration: 'none',
                alignItems: 'center',
                transition: theme.transitions.create(['font-size'], {
                    duration: theme.transitions.duration.short,
                }),
              }}
            >
              DIE BUNTE LIGA
            </Typography>
          </Box>

          {/* --- Mobiles Men√º Icon --- */}
          <Box sx={{ display: { xs: 'flex', md: 'none' }, alignItems: 'center'}}>
            <IconButton
              size="large"
              aria-label="open navigation menu"
              onClick={handleDrawerToggle}
              color="inherit"
            >
              <MenuIcon />
            </IconButton>
          </Box>
          
          <Drawer
            variant="temporary"
            anchor="right"
            open={mobileOpen}
            onClose={handleDrawerToggle}
            ModalProps={{ keepMounted: true }}
            sx={{
              display: { xs: 'block', md: 'none' },
              '& .MuiDrawer-paper': { boxSizing: 'border-box', width: drawerWidth },
            }}
          >
            {drawerContent}
          </Drawer>

          {/* --- Desktop Navigation Buttons --- */}
          <Box sx={{ flexGrow: 1, display: { xs: 'none', md: 'flex' }, justifyContent: 'flex-end' }}>
            {pages.map((pageItem) => (
              <Button
                key={pageItem.text}
                sx={{ 
                  my: isShrunk ? 0.5 : 2, 
                  color: 'white', 
                  display: 'block', 
                  '&:hover': { backgroundColor: 'rgba(255, 255, 255, 0.36)' },
                  transition: theme.transitions.create(['margin-top', 'margin-bottom'], {
                    duration: theme.transitions.duration.short,
                  }),
                }}
                className="rounded-md"
                onClick={() => { navigate(pageItem.path);}}
              >
                {pageItem.text}
              </Button>
            ))}
          </Box>
        </Toolbar>
      </Container>
    </AppBar>
  );
}
export default Header;
