import * as React from 'react';
import {
  Table,
  TableBody,
  TableCell,
  TableContainer,
  Paper,
  Box,
  Typography,
  Avatar,
  useTheme,
  Container,
  useMediaQuery,
} from '@mui/material';

// Hilfsfunktion zur Erstellung der Saisondaten
const createSeasonData = (id, year, championName, championLogoColor) => {
  return {
    id,
    year,
    championName,
    championLogoColor,
  };
};

// Beispieldaten: Meister pro Saison
// Die Daten sind bereits absteigend nach Jahr sortiert
const seasonData = [
  createSeasonData(1, 2024, 'Rakete Freiburg', '#D32F2F'),
  createSeasonData(2, 2023, 'Lokomotive Littenweiler', '#5D4037'),
  createSeasonData(3, 2022, 'Rakete Freiburg', '#D32F2F'),
  createSeasonData(4, 2021, 'Die 2. Wahl', '#757575'),
  createSeasonData(5, 2020, 'Rakete Freiburg', '#D32F2F'),
  createSeasonData(6, 2019, 'Lokomotive Littenweiler', '#5D4037'),
  createSeasonData(7, 2018, 'Red Sea', '#C2185B'),
  createSeasonData(8, 2017, 'Dynamo Tresen', '#1976D2'),
];

const SeasonChampionsList = ({ title = "Chronik der Meister" }) => {
  const theme = useTheme();
  const isMobile = useMediaQuery(theme.breakpoints.down('sm'));

  return (
    <Container maxWidth="md" sx={{ my: 4, px: isMobile ? 1 : 2 }}>
      <Typography
        variant={isMobile ? 'h5' : 'h4'}
        sx={{
          mb: 3,
          mt: 2,
          color: '#00A99D',
          fontWeight: 700,
          fontFamily: 'comfortaa',
          textAlign: 'center',
          textTransform: 'uppercase',
          letterSpacing: '0.1em',
        }}
      >
        {title}
      </Typography>
      <Paper
        sx={{
          backgroundColor: '#111',
          borderRadius: theme.shape.borderRadius,
          border: `1px solid ${theme.palette.grey[800]}`,
        }}
      >
        <TableContainer>
          <Table aria-label="Chronik der Meister">
            {/* Kein TableHead f√ºr ein minimalistisches Design */}
            <TableBody>
              {seasonData.map((season) => (
                <TableRow
                  key={season.id}
                  sx={{ '&:hover': { backgroundColor: 'rgba(255,255,255,0.08)' } }}
                >
                  <TableCell sx={{ width: isMobile? '25%' : '30%', borderBottomColor: 'grey.800', py: 2 }}>
                    <Typography sx={{ fontFamily: 'comfortaa', color: theme.palette.grey[100], fontWeight: 'bold', fontSize: isMobile ? '0.9rem' : '1.1rem' }}>
                      {season.year}
                    </Typography>
                  </TableCell>
                  <TableCell sx={{ width: isMobile? '75%' : '70%', borderBottomColor: 'grey.800', py: 2 }}>
                    <Box sx={{ display: 'flex', alignItems: 'center' }}>
                      <Avatar
                        alt={`${season.championName} Logo`}
                        sx={{
                          width: isMobile ? 28 : 32,
                          height: isMobile ? 28 : 32,
                          mr: isMobile ? 1.5 : 2,
                          fontSize: isMobile ? '0.7rem' : '0.8rem',
                          color: theme.palette.getContrastText(season.championLogoColor || theme.palette.grey[700]),
                          backgroundColor: season.championLogoColor || theme.palette.grey[700],
                        }}
                      >
                        {season.championName.substring(0, 1).toUpperCase()}
                      </Avatar>
                      <Typography noWrap sx={{ fontFamily: 'comfortaa', color: theme.palette.grey[200], fontSize: isMobile ? '0.8rem' : '1rem' }}>
                        {season.championName}
                      </Typography>
                    </Box>
                  </TableCell>
                </TableRow>
              ))}
            </TableBody>
          </Table>
        </TableContainer>
      </Paper>
    </Container>
  );
};

export default SeasonChampionsList;
