import * as React from 'react';
import AppBar from '@mui/material/AppBar';
import Box from '@mui/material/Box';
import Toolbar from '@mui/material/Toolbar';
import IconButton from '@mui/material/IconButton';
import Typography from '@mui/material/Typography';
import MenuIcon from '@mui/icons-material/Menu';
import Container from '@mui/material/Container';
import Button from '@mui/material/Button';
import Drawer from '@mui/material/Drawer';
import ChevronRightIcon from '@mui/icons-material/ChevronRight';
import ChevronLeftIcon from '@mui/icons-material/ChevronLeft';
import ListItem from '@mui/material/ListItem';
import ListItemButton from '@mui/material/ListItemButton';
import ListItemIcon from '@mui/material/ListItemIcon';
import ListItemText from '@mui/material/ListItemText';
import Divider from '@mui/material/Divider';
import List from '@mui/material/List';
import { styled, useTheme } from '@mui/material/styles';

import HomeIcon from '@mui/icons-material/Home';
import TableViewIcon from '@mui/icons-material/TableView';
import Diversity3Icon from '@mui/icons-material/Diversity3';
import ScheduleIcon from '@mui/icons-material/Schedule';
import PermContactCalendarIcon from '@mui/icons-material/PermContactCalendar';
import ListIcon from '@mui/icons-material/List';
import { useNavigate, useLocation } from 'react-router-dom';
import logo from '../img/logo.png'; // Stellen Sie sicher, dass der Pfad korrekt ist

const pages = [
  { text: 'Home', iconName: 'HomeIcon', path: '/' },
  { text: 'Ergebnisse', iconName: 'TableViewIcon', path: '/ergebnisse' },
  { text: 'Teams', iconName: 'Diversity3Icon', path: '/teams' },
  { text: 'Historiker-Ecke', iconName: 'ScheduleIcon', path: '/historie' },
  { text: 'Kontakt', iconName: 'PermContactCalendarIcon', path: '/kontakt' },
];

const iconMap = {
  HomeIcon: HomeIcon,
  TableViewIcon: TableViewIcon,
  Diversity3Icon: Diversity3Icon,
  ScheduleIcon: ScheduleIcon,
  PermContactCalendarIcon: PermContactCalendarIcon,
  DefaultIcon: ListIcon,
};

const drawerWidth = 240;

// Desktop Logo Größen
const initialLogoHeight = 40;
const shrunkLogoHeight = 30;
const initialLogoWidth = 50;
const shrunkLogoWidth = 40;

// Mobile Logo Größen
const mobileInitialLogoHeight = 35;
const mobileShrunkLogoHeight = 28;
const mobileInitialLogoWidth = 45;
const mobileShrunkLogoWidth = 38;

// AppBar Hintergrundfarbe für Konsistenz
const appBarBackgroundColor = 'rgba(0, 0, 0, 0.85)'; // Etwas angepasst für Lesbarkeit, kann auch '#000' sein
const appBarBlur = 'blur(8px)';


const CustomDrawerHeader = styled('div')(({ theme }) => ({
  display: 'flex',
  alignItems: 'center',
  padding: theme.spacing(0, 1),
  // necessary for content to be below app bar
  ...theme.mixins.toolbar,
  justifyContent: 'flex-start',
}));

function Header() {
  const navigate = useNavigate();
  const theme = useTheme();
  const location = useLocation(); 
  const [mobileOpen, setMobileOpen] = React.useState(false);
  const [isShrunk, setIsShrunk] = React.useState(false);

  React.useEffect(() => {
    const handleScroll = () => {
      if (window.scrollY > 50) { // Schwellenwert für das Schrumpfen
        setIsShrunk(true);
      } else {
        setIsShrunk(false);
      }
    };
    window.addEventListener('scroll', handleScroll);
    return () => {
      window.removeEventListener('scroll', handleScroll);
    };
  }, []);

  const handleDrawerToggle = () => {
    setMobileOpen(!mobileOpen);
  };
  
  const drawerContent = (
      <Box
        sx={{
          width: drawerWidth,
          backgroundColor: appBarBackgroundColor,
          backdropFilter: appBarBlur,
          color: theme.palette.common.white,
          height: '100%',
          display: 'flex',
          flexDirection: 'column',
        }}
      >
        <CustomDrawerHeader sx={{ justifyContent: 'flex-end' }}>
          <IconButton onClick={handleDrawerToggle} sx={{ color: theme.palette.common.white }}>
            {theme.direction === 'rtl' ? <ChevronLeftIcon /> : <ChevronRightIcon />}
          </IconButton>
        </CustomDrawerHeader>
        <Divider sx={{ borderColor: theme.palette.grey[700] }} />
        <List sx={{ flexGrow: 1 }}>
          {pages.map((pageItem) => {
            const IconComponent = iconMap[pageItem.iconName] || iconMap.DefaultIcon;
            const isActive = location.pathname === pageItem.path;
            return (
              <ListItem key={pageItem.text} disablePadding>
                <ListItemButton
                  onClick={() => { navigate(pageItem.path); handleDrawerToggle(); }}
                  sx={{ 
                    backgroundColor: isActive ? theme.palette.primary.main : 'transparent',
                    '&:hover': {
                      backgroundColor: isActive ? theme.palette.primary.dark : theme.palette.grey[800],
                    },
                    borderRadius: theme.shape.borderRadius,
                    margin: theme.spacing(0.5, 1),
                  }}
                >
                  <ListItemIcon sx={{ color: isActive ? theme.palette.common.white : theme.palette.grey[400], minWidth: 'auto', mr: 1.5 }}>
                    {IconComponent ? <IconComponent fontSize="small" /> : null}
                  </ListItemIcon>
                  <ListItemText 
                    primary={pageItem.text} 
                    primaryTypographyProps={{ 
                        fontWeight: isActive ? 'bold' : 'normal',
                        color: isActive ? theme.palette.common.white : theme.palette.grey[300],
                    }} 
                  />
                </ListItemButton>
              </ListItem>
            );
          })}
        </List>
      </Box>
  );

  return (
    <AppBar 
        position="sticky" 
        sx={{ 
            backgroundColor: '#000', 
            backdropFilter: appBarBlur,
            boxShadow: isShrunk ? theme.shadows[4] : theme.shadows[1] 
        }}
    >
      <Container maxWidth="xl">
        <Toolbar 
          disableGutters 
          sx={{ 
            minHeight: { 
                xs: isShrunk ? 48 : 56, 
                md: isShrunk ? 50 : 70 
            }, 
            transition: theme.transitions.create(['min-height', 'padding'], {
              duration: theme.transitions.duration.short,
            }),
            py: { xs: isShrunk ? 0.25 : 0.5, md: isShrunk ? 0.25 : 0.5 }, 
          }}
        >

          {/* --- Desktop Ansicht Logo & Titel --- */}
          <Box 
            sx={{ display: { xs: 'none', md: 'flex' }, mr: 1, alignItems: 'center', cursor: 'pointer', flexShrink: 0 }} 
            onClick={() => navigate('/')}
          >
            <img
              src={logo} 
              alt="Bunte Liga Freiburg Logo"
              style={{ 
                height: isShrunk ? `${shrunkLogoHeight}px` : `${initialLogoHeight}px`, 
                width: 'auto',
                maxHeight: isShrunk ? shrunkLogoHeight : initialLogoHeight, 
                objectFit: 'contain',
                transition: 'height 0.3s ease-in-out, max-height 0.3s ease-in-out',
              }}
            />
          </Box>
          <Typography
            variant={isShrunk ? "h6" : "h5"}
            noWrap
            component="div"
            sx={{
              mr: 2,
              display: { xs: 'none', md: 'flex' },
              fontFamily: 'monospace',
              fontWeight: 700,
              letterSpacing: '.2rem',
              color: 'inherit',
              textDecoration: 'none',
              alignItems: 'center',
              transition: theme.transitions.create(['font-size'], {
                duration: theme.transitions.duration.short,
              }),
            }}
          >
            DIE BUNTE LIGA
          </Typography>
          
          {/* --- Mobile Ansicht Logo & Titel --- */}
          <Box sx={{ flexGrow: 1, display: { xs: 'flex', md: 'none' }, alignItems: 'center', justifyContent: 'flex-start' }}>
            <Box sx={{ display: 'flex', alignItems: 'center', mx: 1, cursor: 'pointer', flexShrink: 0 }} onClick={() => navigate('/')}>
                <img
                  src={logo} 
                  alt="Bunte Liga Freiburg Logo Mobile"
                  style={{ 
                    height: `${isShrunk ? mobileShrunkLogoHeight : mobileInitialLogoHeight}px`, 
                    width: 'auto',
                    maxHeight: isShrunk ? mobileShrunkLogoHeight : mobileInitialLogoHeight,
                    objectFit: 'contain',
                    transition: 'height 0.3s ease-in-out, max-height 0.3s ease-in-out',
                  }}
                />
            </Box>
            <Typography
              variant={isShrunk ? 'body1' : 'h6' }
              noWrap
              component="div"
              sx={{
                display: 'flex', 
                fontFamily: 'monospace',
                fontWeight: 700,
                letterSpacing: '0.05rem',
                color: 'inherit',
                textDecoration: 'none',
                alignItems: 'center',
                overflow: 'hidden',
                textOverflow: 'ellipsis', 
                transition: theme.transitions.create(['font-size'], {
                    duration: theme.transitions.duration.short,
                }),
              }}
            >
              DIE BUNTE LIGA
            </Typography>
          </Box>

          {/* --- Mobiles Menü Icon --- */}
          <Box sx={{ display: { xs: 'flex', md: 'none' }, alignItems: 'center', ml: 'auto' /* Schiebt Icon nach rechts */ }}>
            <IconButton
              size="large"
              aria-label="Menü öffnen"
              onClick={handleDrawerToggle}
              color="inherit"
            >
              <MenuIcon sx={{ fontSize: isShrunk ? '1.6rem' : '1.85rem', transition: 'font-size 0.3s ease-in-out' }} />
            </IconButton>
          </Box>
          
          {/* --- Drawer für mobile Navigation --- */}
          <Drawer
            variant="temporary"
            anchor="left"
            open={mobileOpen}
            onClose={handleDrawerToggle}
            ModalProps={{ keepMounted: true }}
            sx={{
              display: { xs: 'block', md: 'none' },
              '& .MuiDrawer-paper': { 
                boxSizing: 'border-box', 
                width: drawerWidth,
                backgroundColor: appBarBackgroundColor, 
                backdropFilter: appBarBlur,
                color: theme.palette.common.white,
                borderLeft: `1px solid ${theme.palette.grey[800]}`
              },
            }}
          >
            {drawerContent}
          </Drawer>

          {/* --- Desktop Navigation Buttons --- */}
          <Box sx={{ flexGrow: 1, display: { xs: 'none', md: 'flex' }, justifyContent: 'flex-end', alignItems: 'center' }}>
            {pages.map((pageItem) => {
              const isActive = location.pathname === pageItem.path;
              return (
                <Button
                  key={pageItem.text}
                  onClick={() => { navigate(pageItem.path);}}
                  sx={{ 
                    my: isShrunk ? 0.1 : 0.5, 
                    mx: isShrunk ? 0.3 : 0.5, 
                    color: isActive ? theme.palette.common.white : 'rgba(255, 255, 255, 0.75)', 
                    display: 'block', 
                    fontSize: isShrunk ? '0.78rem' : '0.9rem', 
                    fontWeight: isActive ? 'bold' : 500, 
                    borderBottom: isActive ? `3px solid ${theme.palette.primary.main}` : '3px solid transparent',
                    borderRadius: '4px 4px 0 0', 
                    '&:hover': { 
                      backgroundColor: 'rgba(255, 255, 255, 0.08)',
                      color: theme.palette.common.white, 
                      borderBottom: isActive ? `3px solid ${theme.palette.primary.light}` : `3px solid rgba(255, 255, 255, 0.15)`,
                    },
                    transition: theme.transitions.create(['margin', 'font-size', 'color', 'border-bottom', 'padding', 'background-color'], {
                      duration: theme.transitions.duration.short, // Kürzere Duration für snappy feel
                    }),
                    padding: isShrunk ? '4px 10px' : '6px 14px', 
                    minWidth: 'auto', 
                    textTransform: 'none', 
                  }}
                >
                  {pageItem.text}
                </Button>
              );
            })}
          </Box>
        </Toolbar>
      </Container>
    </AppBar>
  );
}
export default Header;
